INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-008-reunion-tower', 'Даллас — башня Reunion Tower', 'Игрок поднимается на GeO-Deck, анализирует skyline в AR-режиме и выбирает, инвестировать ли в архитектурные экскурсии или частные мероприятия.', 'side', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_elevator_access", "text": "Получить доступ на лифт и собрать данные о времени подъёма", "type": "interact", "target": "reunion_tower_elevator", "count": 1, "optional": false}, {"id": "use_binoculars", "text": "Использовать интерактивные бинокли, чтобы отметить ключевые районы для архитектурной линии", "type": "interact", "target": "interactive_binoculars", "count": 3, "optional": false}, {"id": "choose_event", "text": "Выбрать: запустить ночное световое шоу или организовать частную вечеринку", "type": "dialogue", "target": "event_planner_npc", "count": 1, "optional": false}, {"id": "update_map", "text": "Спуститься и обновить карту туристических маршрутов города", "type": "interact", "target": "tourist_map_station", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "money": -20, "reputation": {"aesthetic": 15}, "items": [], "unlocks": {"achievements": ["Покоритель шара"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-008-reunion-tower", "title": "Даллас — башня Reunion Tower", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:55:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "landmark", "tourism"], "topics": ["city_storylines", "urban_landmarks"], "related_systems": ["narrative-service", "exploration-service", "social-service"], "related_documents": [{"id": "master-timeline", "relation": "references"}, {"id": "quest-system", "relation": "aligns"}, {"id": "github-issue-333", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/333", "relation": "migrated_to", "migrated_at": "2025-11-22T19:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/dallas/2020-2029/quest-008-reunion-tower.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исходный текст не показывал, как смотровая площадка и ресторан влияют на экономику и социальные сцены города.", "goal": "Связать посещение Reunion Tower с системой туристических баффов и архитектурными событиями.", "essence": "Игрок поднимается на GeO-Deck, анализирует skyline в AR-режиме и выбирает, инвестировать ли в архитектурные экскурсии или частные мероприятия.", "key_points": ["Башня используется как интерфейс для скрытых архитектурных историй города.", "Решения игрока влияют на поток туристов и доступ к приватным событиям.", "Появляется ветка с повторяемыми заданиями по ночным фотосессиям."]}, "quest_definition": {"quest_type": "side", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_elevator_access", "text": "Получить доступ на лифт и собрать данные о времени подъёма", "type": "interact", "target": "reunion_tower_elevator", "count": 1, "optional": false}, {"id": "use_binoculars", "text": "Использовать интерактивные бинокли, чтобы отметить ключевые районы для архитектурной линии", "type": "interact", "target": "interactive_binoculars", "count": 3, "optional": false}, {"id": "choose_event", "text": "Выбрать: запустить ночное световое шоу или организовать частную вечеринку", "type": "dialogue", "target": "event_planner_npc", "count": 1, "optional": false}, {"id": "update_map", "text": "Спуститься и обновить карту туристических маршрутов города", "type": "interact", "target": "tourist_map_station", "count": 1, "optional": false}], "rewards": {"experience": 1000, "money": -20, "reputation": {"aesthetic": 15}, "items": [], "unlocks": {"achievements": ["Покоритель шара"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается у основания Reunion Tower, где игрок взаимодействует с AR-гидом. За 68 секунд лифт доставляет на\\nGeO-Deck, а интерактивные панели раскрывают историю строительства. Игрок фиксирует панорамы, выбирает планы инвестиций\\nи решает, участвовать ли в вращающемся ужине.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Подчёркнут 171-метровый силуэт башни, прозвище «The Ball», 360-градусный обзор и вращающийся ресторан. Интерфейсы\\nпоясняют роль постройки в формировании skyline Далласа.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Получить доступ на лифт и собрать данные о времени подъёма.\\n2. Использовать интерактивные бинокли, чтобы отметить ключевые районы для архитектурной линии.\\n3. Выбрать: запустить ночное световое шоу или организовать частную вечеринку.\\n4. Спуститься и обновить карту туристических маршрутов города.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест даёт 1000 опыта, требует 20 единиц валюты и повышает эстетический показатель на 15. Ачивка «Покоритель шара»\\nоткрывает цепочку ночных экскурсий и скидки для фракции архитекторов.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Exploration-service обновляет маршруты и баффы обзора, narrative-service добавляет события о городской панораме,\\nа social-service сохраняет репутацию в клубе архитекторов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1180, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Структурирован квест «Башня Reunion Tower» с туристическими и архитектурными интеграциями."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

