-- Quest: quest-tbilisi-2029-old-town-walk
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-tbilisi-2029-old-town-walk', 'Тбилиси 2020-2029 — «Прогулка по Старому Тбилиси»', 'Игрок исследует кварталы, посещает серные бани, поднимается к Нарикале и завершает маршрут у статуи «Мать-Грузия».', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "start_old_town", "text": "Начать прогулку в Старом Тбилиси и получить AR-карту", "type": "visit", "target": "old_tbilisi_district", "count": 1, "optional": false}, {"id": "explore_streets", "text": "Исследовать старые улочки с деревянными балконами", "type": "explore", "target": "old_tbilisi_streets", "count": 5, "optional": false}, {"id": "visit_baths", "text": "Посетить серные бани Абанотубани", "type": "visit", "target": "abanotubani_baths", "count": 1, "optional": false}, {"id": "spa_session", "text": "Принять процедуру в серных банях", "type": "interact", "target": "sulfur_bath_session", "count": 1, "optional": true}, {"id": "ascend_narikala", "text": "Подняться к крепости Нарикала (пешком или на канатной дороге)", "type": "travel", "target": "narikala_fortress", "count": 1, "optional": false}, {"id": "explore_fortress", "text": "Исследовать крепость Нарикала и её историю", "type": "explore", "target": "narikala_exploration", "count": 1, "optional": false}, {"id": "mother_georgia", "text": "Достичь статуи Мать-Грузия и изучить её символизм", "type": "visit", "target": "mother_georgia_statue", "count": 1, "optional": false}, {"id": "panorama_photo", "text": "Сделать панорамное фото города (опционально)", "type": "interact", "target": "city_panorama_photo", "count": 1, "optional": true}]'::jsonb, '{"experience": 1000, "money": -30, "items": [{"item_id": "achievement_tbilisi_keeper", "quantity": 1}], "reputation": {"tbilisi_locals": 5, "georgian_culture": 10}, "unlocks": ["old_tbilisi_tours_premium"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-tbilisi-2029-old-town-walk", "title": "Тбилиси 2020-2029 — «Прогулка по Старому Тбилиси»", "document_type": "canon", "category": "timeline-quest", "status": "in_review", "version": "1.1.0", "last_updated": "2025-11-29T10:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T10:00:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tbilisi", "sightseeing", "wellness"], "topics": ["cultural-exploration", "light-adventure"], "related_systems": ["narrative-service", "travel-service", "social-service"], "related_documents": [{"id": "github-issue-773", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/773", "relation": "migrated_to", "migrated_at": "2025-11-23T00:32:09+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-001-old-tbilisi-walk.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристической ветке Тбилиси требовалась атмосферная прогулка по историческому центру.", "goal": "Провести игрока по ключевым точкам Старого Тбилиси с элементами релаксации и истории.", "essence": "Игрок исследует кварталы, посещает серные бани, поднимается к Нарикале и завершает маршрут у статуи «Мать-Грузия».", "key_points": ["Канатная дорога и панорама города создают кульминацию маршрута.", "Серные бани дают механический бафф и раскрывают этимологию названия города.", "Квест связывает архитектурные и культурные линии Тбилиси."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "start_old_town", "text": "Начать прогулку в Старом Тбилиси и получить AR-карту", "type": "visit", "target": "old_tbilisi_district", "count": 1, "optional": false}, {"id": "explore_streets", "text": "Исследовать старые улочки с деревянными балконами", "type": "explore", "target": "old_tbilisi_streets", "count": 5, "optional": false}, {"id": "visit_baths", "text": "Посетить серные бани Абанотубани", "type": "visit", "target": "abanotubani_baths", "count": 1, "optional": false}, {"id": "spa_session", "text": "Принять процедуру в серных банях", "type": "interact", "target": "sulfur_bath_session", "count": 1, "optional": true}, {"id": "ascend_narikala", "text": "Подняться к крепости Нарикала (пешком или на канатной дороге)", "type": "travel", "target": "narikala_fortress", "count": 1, "optional": false}, {"id": "explore_fortress", "text": "Исследовать крепость Нарикала и её историю", "type": "explore", "target": "narikala_exploration", "count": 1, "optional": false}, {"id": "mother_georgia", "text": "Достичь статуи Мать-Грузия и изучить её символизм", "type": "visit", "target": "mother_georgia_statue", "count": 1, "optional": false}, {"id": "panorama_photo", "text": "Сделать панорамное фото города (опционально)", "type": "interact", "target": "city_panorama_photo", "count": 1, "optional": true}], "rewards": {"experience": 1000, "money": -30, "items": [{"item_id": "achievement_tbilisi_keeper", "quantity": 1}], "reputation": {"tbilisi_locals": 5, "georgian_culture": 10}, "unlocks": ["old_tbilisi_tours_premium"]}, "branches": []}, "content": {"sections": [{"id": "lore_context", "title": "Лор и контекст", "body": "Тбилиси (в переводе с грузинского — \\"тёплый\\") получил своё название от серных источников, которые бьют \\nиз-под земли на территории города уже более 1500 лет. Легенда гласит, что в V веке царь Вахтанг Горгасали \\nохотился на фазана в этих местах. Фазан упал в горячий серный источник, и царь решил основать здесь город.\\n\\nСтарый Тбилиси (Дзвели Тбилиси) — исторический центр города, где сохранились деревянные балконы, узкие \\nулочки и атмосфера древней столицы. В 2020-х годах этот район стал культурным центром, где традиции \\nсоседствуют с современными технологиями AR-гидов и цифровых реконструкций.\\n\\nКрепость Нарикала (IV век) — символ древности Тбилиси, защищавшая город от нашествий. Статуя \\"Мать-Грузия\\" \\n(Картлис Деда) возвышается над городом — в одной руке она держит чашу с вином для друзей, в другой меч \\nдля врагов, символизируя гостеприимство и готовность к защите.\\n", "mechanics_links": [], "assets": []}, {"id": "npcs", "title": "Персонажи квеста", "body": "**Леван Такаишвили** — AR-гид и хранитель истории Старого Тбилиси. Пожилой человек с богатой памятью, \\nзнает каждую улицу и каждый дом. Ведёт игрока по маршруту, рассказывая легенды и истории.\\n\\n**Николоз Абанотубани** — хозяин серных бань, потомственный банщик. Мудрый, с чувством юмора, \\nзнает секреты омоложения. Рассказывает об истории бань и их целебных свойствах.\\n\\n**Мариам Нарикала** — хранительница крепости, археолог и историк. Молодая и страстная исследовательница, \\nпроводит экскурсии по крепости с VR-реконструкциями древних боёв.\\n", "mechanics_links": [], "assets": []}, {"id": "entry", "title": "Старые улочки", "body": "Игрок начинает прогулку в районе Старого Тбилиси, где встречает Левана Такаишвили — местного гида и \\nхранителя истории. Леван предлагает игроку AR-карту старого города, которая активируется при приближении \\nк историческим зданиям.\\n\\n**AR-гид и исследование:**\\n- AR-карта показывает 5 ключевых точек: Дом ремесленников, Деревянный балкон, Караван-сарай, Церковь \\n  Анчисхати, Дом с резьбой\\n- При приближении к каждой точке активируется аудиогид с историей здания\\n- Игрок может собирать виртуальные артефакты (исторические фотографии, документы) для коллекции\\n\\n**Деревянные балконы:**\\nЛеван объясняет, что резные деревянные балконы — визитная карточка старого Тбилиси. В 2020-х многие \\nбалконы отреставрированы с сохранением традиционного стиля, но добавлены элементы киберпанка — AR-проекции, \\nцифровые вывески, неоновые подсветки.\\n", "mechanics_links": ["mechanics/exploration/guided-tour.yaml", "mechanics/ar/ar-navigation.yaml"], "assets": []}, {"id": "baths", "title": "Серные бани Абанотубани", "body": "Игрок прибывает в район Абанотубани (\\"Квартал бань\\"), где серные источники бьют прямо из-под земли. \\nЗдесь находится банный комплекс, где столетиями лечили болезни и восстанавливали силы.\\n\\n**Встреча с Николозом:**\\nНиколоз Абанотубани встречает игрока у входа в банный комплекс. Он рассказывает историю: \\"Тбилиси — \\nединственный город в мире, где серные бани работают на природных источниках уже полторы тысячи лет. \\nНаши предки верили, что эти воды исцеляют душу и тело.\\"\\n\\n**Мини-игра релаксации:**\\nИгрок может выбрать уровень процедуры:\\n- **Базовая** (30 эдди) — краткое посещение, бафф \\"Sulfur Relax\\" +10% HP на 2 часа\\n- **Стандартная** (50 эдди) — полная процедура, бафф +15% HP и +10% выносливости на 4 часа\\n- **Премиум** (100 эдди) — процедура с массажем, бафф +20% HP и +15% выносливости на 6 часов\\n\\n**История бань:**\\nНиколоз показывает древние купола бань и рассказывает легенды. В киберпанк-версии 2020-х бани модернизированы: \\nдобавлены цифровые панели для контроля температуры, AR-проекции для релаксации, но сохранена аутентичная \\nатмосфера и серный запах, который и дал название городу.\\n", "mechanics_links": ["mechanics/wellbeing/spa-session.yaml", "mechanics/progression/buff-system.yaml"], "assets": []}, {"id": "ascent", "title": "Подъём к Нарикале", "body": "После бань игрок должен подняться к крепости Нарикала, которая возвышается над городом. Есть два пути:\\n\\n**Выбор маршрута:**\\n- **Канатная дорога** (50 эдди) — быстрый подъём с панорамным видом. Игрок видит весь город в VR-окнах, \\n  где проецируются исторические слои Тбилиси от древности до 2020-х\\n- **Пеший подъём** (бесплатно) — медленный маршрут через парк Рике. Игрок открывает скрытые видовые точки, \\n  собирает цветы для коллекции, встречает местных жителей. Дополнительный опыт +200 XP\\n\\n**Исследование крепости:**\\nУ входа в крепость игрока встречает Мариам Нарикала — археолог и хранительница. Она проводит экскурсию:\\n- Крепость IV века, пережившая множество осад\\n- VR-реконструкция боёв 1795 года (разрушение персами)\\n- Тайные ходы и подземные казематы (опциональное исследование)\\n- Церковь Святого Николая внутри крепости\\n\\nМариам рассказывает: \\"Нарикала — не просто крепость, это сердце Тбилиси. Каждый камень помнит историю \\nнашего народа. В 2020-х мы используем VR, чтобы показать, как жили защитники крепости.\\"\\n", "mechanics_links": ["mechanics/exploration/photo-checkpoints.yaml", "mechanics/ar/vr-reconstruction.yaml", "mechanics/exploration/hidden-locations.yaml"], "assets": []}, {"id": "finale", "title": "Панорама у «Матери-Грузии»", "body": "Финальный этап — подъём к статуе \\"Мать-Грузия\\" (Картлис Деда), которая возвышается на вершине горы. \\nСтатуя была установлена в 1958 году и стала символом Тбилиси.\\n\\n**Символизм статуи:**\\nМариам объясняет смысл символов:\\n- **Меч в правой руке** — готовность защитить страну от врагов\\n- **Чаша вина в левой руке** — гостеприимство для друзей\\n- \\"Грузия всегда открыта для гостей, но готова к бою, если это необходимо\\"\\n\\n**Выбор игрока:**\\nИгрок может \\"предложить дар\\" статуе (символический выбор):\\n- **Дар мира** — положить цветок у ног статуи, получить бафф \\"Гостеприимство\\" +10% к социальным взаимодействиям\\n- **Дар защиты** — коснуться меча, получить бафф \\"Решительность\\" +5% к урону в защите\\n- **Дар вина** — символически выпить из чаши, получить бафф \\"Грузинская душа\\" +15% к культурной репутации\\n\\n**Панорама города:**\\nС обзорной площадки открывается панорама всего Тбилиси:\\n- Старый город с красными крышами\\n- Современные небоскрёбы в центре\\n- Река Кура, протекающая через город\\n- Горы, окружающие столицу\\n\\n**Опциональное фотозадание:**\\nИгрок может сделать панорамное фото города. Если фото получается удачным (мини-игра), получает \\nдополнительную награду — цифровой арт \\"Тбилиси 2020-х\\" для коллекции.\\n", "mechanics_links": ["mechanics/narrative/cultural-briefing.yaml", "mechanics/exploration/photo-checkpoints.yaml", "mechanics/progression/buff-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "**Основные награды:**\\n- 1 000 XP за прохождение квеста\\n- Расходы: -30 эдди (посещение бань, опционально канатная дорога -50 эдди)\\n- Бафф «Тбилисская гармония» +15% к культуре на 24 часа\\n- Репутация: +5 к \\"Жители Тбилиси\\", +10 к \\"Грузинская культура\\"\\n\\n**Достижения:**\\n- \\"Хранитель Тбилиси\\" — базовое достижение за завершение квеста\\n- \\"Исследователь крепости\\" — если игрок исследовал все скрытые ходы в Нарикале\\n- \\"Мастер фотографии\\" — если удалось сделать идеальное панорамное фото\\n\\n**Разблокировки:**\\n- Доступ к премиум-турам по Старому Тбилиси (LiveOps события)\\n- Скидка 20% на серные бани при повторном посещении\\n- Открытие квеста \\"Колыбель вина\\" (последовательность)\\n", "mechanics_links": ["mechanics/progression/achievement-grant.yaml", "mechanics/progression/buff-system.yaml", "mechanics/reputation/reputation-system.yaml"], "assets": []}, {"id": "connections", "title": "Связи с другими квестами", "body": "Квест является частью культурной линии Тбилиси и связан с другими квестами:\\n- **Колыбель вина** (quest-003) — следующий культурный квест про виноделие Кахетии\\n- **Хинкали-мастер** (quest-002) — гастрономический квест про грузинскую кухню\\n- **Супра** (quest-004) — квест про традиционное застолье\\n\\nЗавершение этого квеста открывает доступ к премиум-контенту и улучшает отношения с местными жителями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 773, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Прогулка по Старому Тбилиси» адаптирован под шаблон Concept Director."}, {"version": "1.1.0", "date": "2025-11-29", "author": "idea-writer", "changes": "Детально доработан квест Idea Writer:\\n- Добавлен детальный лор о Тбилиси, серных банях и крепости Нарикала\\n- Создана структура quest_definition с 8 объективами\\n- Добавлены описания NPC (Леван, Николоз, Мариам)\\n- Расширены секции с детальным описанием механик:\\n  - Старые улочки (AR-гид, виртуальные артефакты)\\n  - Серные бани (мини-игра релаксации, выбор процедур)\\n  - Подъём к Нарикале (выбор маршрута, VR-реконструкции)\\n  - Статуя Мать-Грузия (символизм, выбор дара)\\n- Детализированы награды и разблокировки\\n- Добавлены связи с другими квестами\\n- Добавлены нарративные заметки для Content Writer\\n"}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

