INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-tbilisi-2029-supra-feast', 'Тбилиси 2020-2029 — «Грузинская супра»', 'Игрок принимает приглашение, учится ритуалам, произносит тосты и взаимодействует с гостями.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "receive_supra_invitation", "text": "Получить приглашение от грузинской семьи, пройти короткий бриф об этикете и подготовить символический подарок", "type": "interact", "target": "georgian_family_invitation", "count": 1, "optional": false}, {"id": "participate_feast", "text": "Участвовать в застолье, управлять насыщением и аккуратно сочетать блюда: хинкали, хачапури, сациви и шашлык", "type": "consume", "target": "supra_feast_table", "count": 1, "optional": false}, {"id": "respond_to_toasts", "text": "Отвечать на тосты тамады мини-игрой импровизации, выбирая темы семьи, друзей и страны", "type": "perform", "target": "tamada_toasts_ceremony", "count": 1, "optional": false}, {"id": "join_music_dance", "text": "Участвовать в многоголосных песнях и танцах для получения дополнительных очков социального статуса", "type": "perform", "target": "polyphonic_singing_dance", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "currency": 0, "items": [{"id": "hospitality_buff", "name": "Бафф «Гостеприимство» +50% XP на 48 часов", "quantity": 1}], "reputation": {"tbilisi_social": 20}, "flags": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-tbilisi-2029-supra-feast", "title": "Тбилиси 2020-2029 — «Грузинская супра»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tbilisi", "supra", "social-event"], "topics": ["community-bonding", "cultural-tradition"], "related_systems": ["narrative-service", "social-service", "wellbeing-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-004-supra-feast.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальному контенту Тбилиси нужна ключевая сцена грузинского застолья.", "goal": "Организовать участие игрока в супре с тамадой, тостами и музыкальной программой.", "essence": "Игрок принимает приглашение, учится ритуалам, произносит тосты и взаимодействует с гостями.", "key_points": ["Тамада задаёт динамику вечера, проверяя харизму игрока.", "Богатый стол и вино дают баффы, но требуют контроля опьянения.", "Гости раскрывают дополнительные квесты и приглашения."]}, "quest_definition": {"quest_type": "side", "level_min": 4, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "receive_supra_invitation", "text": "Получить приглашение от грузинской семьи, пройти короткий бриф об этикете и подготовить символический подарок", "type": "interact", "target": "georgian_family_invitation", "count": 1, "optional": false}, {"id": "participate_feast", "text": "Участвовать в застолье, управлять насыщением и аккуратно сочетать блюда: хинкали, хачапури, сациви и шашлык", "type": "consume", "target": "supra_feast_table", "count": 1, "optional": false}, {"id": "respond_to_toasts", "text": "Отвечать на тосты тамады мини-игрой импровизации, выбирая темы семьи, друзей и страны", "type": "perform", "target": "tamada_toasts_ceremony", "count": 1, "optional": false}, {"id": "join_music_dance", "text": "Участвовать в многоголосных песнях и танцах для получения дополнительных очков социального статуса", "type": "perform", "target": "polyphonic_singing_dance", "count": 1, "optional": false}], "rewards": {"experience": 2000, "currency": 0, "items": [{"id": "hospitality_buff", "name": "Бафф «Гостеприимство» +50% XP на 48 часов", "quantity": 1}], "reputation": {"tbilisi_social": 20}, "flags": []}, "branches": []}, "content": {"sections": [{"id": "invitation", "title": "Приглашение на супру", "body": "Игрок получает приглашение от грузинской семьи, проходит короткий бриф об этикете и готовит символический подарок.\\n", "mechanics_links": ["mechanics/social/event-invitation.yaml"], "assets": []}, {"id": "feast", "title": "Застолье и блюда", "body": "Стол ломится от хинкали, хачапури, сациви и шашлыка. Игрок управляет насыщением и аккуратно сочетает блюда.\\n", "mechanics_links": ["mechanics/wellbeing/feast-management.yaml"], "assets": []}, {"id": "toast", "title": "Тосты тамады", "body": "Тамада ведёт череду тостов. Игрок отвечает мини-игрой импровизации, выбирая темы семьи, друзей и страны.\\n", "mechanics_links": ["mechanics/social/toast-minigame.yaml"], "assets": []}, {"id": "music", "title": "Песни и таницы", "body": "Включаются многоголосные песни и танцы. Участие даёт дополнительные очки социального статуса.\\n", "mechanics_links": ["mechanics/social/performance.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP, социальный рейтинг +20.\\n- Бафф «Гостеприимство» +50% XP на 48 часов.\\n- Уникальные эмоции и приглашения на будущие супры.\\n", "mechanics_links": ["mechanics/progression/buff-grant.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 776, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики сообщества и культурных традиций."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Грузинская супра» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

