', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-punk-concert", "title": "Лондон 2020-2029 — Панк-концерт", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "music", "subculture"], "topics": ["social", "events"], "related_systems": ["narrative-service", "social-system", "event-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2020-2029/quest-004-punk-concert.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сюжет посещения подпольного панк-концерта был только в Markdown.", "goal": "Задокументировать социальное событие, раскрывающее панк-сцену Лондона.", "essence": "Игрок находит вход в подпольный клуб, проходит фейс-контроль и погружается в мини-игры концерта, знакомясь с музыкантами.\\n", "key_points": ["Зафиксированы этапы поиска, проверки стиля и взаимодействий внутри клуба.", {"Определены награды": "опыт, социальные контакты и расходы на билет."}, "Отмечена связь с панк-фракцией и потенциальные сюжетные ветки."]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Заброшенное метро используется панк-сценой как концертная площадка. Игрок получает доступ к подпольному комьюнити.\\n", "mechanics_links": [], "assets": []}, {"id": "event_flow", "title": "Ход события", "body": "1. Найти вход в клуб через сеть контактов.\\n2. Пройти проверку стиля и убедить охрану впустить.\\n3. Участвовать в мини-играх: танцы, барные бои, импровизированные выступления.\\n4. Завести знакомства с музыкантами и организаторами.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "300 XP, минус 500 едди на билет и рост социального капитала в панк-фракции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-punk-concert"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Панк-концерт» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

