INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-lon-big-ben-restoration', 'Лондон 2030-2039 — Восстановление Биг-Бена', 'Игрок собирает исторические чертежи и современные квантовые компоненты, чтобы превратить Биг-Бен в квантовый вычислитель времени и принять решение о его контроле.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-big-ben-restoration", "title": "Лондон 2030-2039 — Восстановление Биг-Бена", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "technology", "heritage"], "topics": ["main_story", "engineering"], "related_systems": ["narrative-service", "infrastructure-system", "faction-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2030-2039/quest-011-big-ben-restoration.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест по восстановлению Биг-Бена как гибридного квантового часовоса находился в Markdown и не был интегрирован в канон.", "goal": "Структурировать сюжет о модернизации лондонского символа в YAML для дальнейшей разработки.", "essence": "Игрок собирает исторические чертежи и современные квантовые компоненты, чтобы превратить Биг-Бен в квантовый вычислитель времени и принять решение о его контроле.\\n", "key_points": ["Описаны шаги по поиску чертежей, сбору пяти компонентов и инсталляции внутри башни.", "Зафиксированы развилки между публичным контролем времени, корпоративной монополией и саботажем.", "Определены награды, влияющие на глобальную механику временных зон Лондона."]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Символ Лондона — Биг-Бен — превращён в техно-артефакт, объединяющий викторианскую эстетику и квантовые вычисления. Игрок становится куратором проекта, от чьих решений зависит будущее городской хронометрии.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Ход квеста", "body": "1. Найти оригинальные чертежи башни в Британском музее и получить допуск к архиву.\\n2. Собрать и откалибровать пять квантовых модулей, отследив их по городу.\\n3. Установить оборудование внутри башни, проходя проверки на технологии и акробатику.\\n4. Активировать часы и выбрать модель управления — общественную, корпоративную или хаотичную.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_elements", "title": "Региональные мотивы", "body": "Викторианская эстетика сочетается с неоновыми вставками, подчёркивая британскую пунктуальность как национальную ценность.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards_consequence", "title": "Награды и последствия", "body": "Игрок получает 2 000 XP, до 50 000 едди и управляет временной механикой Лондона. Выбор влияет на фракции и городские события.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-big-ben-restoration"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Восстановление Биг-Бена» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

