', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-pound-collapse", "title": "Лондон 2040-2060 — Крах фунта", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "economy", "crisis"], "topics": ["main_story", "finance"], "related_systems": ["narrative-service", "economy-system", "progression-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2040-2060/quest-029-pound-collapse.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экономический кризис с переходом на London Coin описывался в Markdown и не был структурирован.", "goal": "Задокументировать основной квест о финансовом крахе и выборе стратегии выживания.", "essence": "Игрок получает предупреждение о падении фунта, принимает решения по управлению капиталом и адаптируется к новой криптовалютной экономике.\\n", "key_points": ["Описаны этапы кризиса от паники до последствий перехода на London Coin.", {"Зафиксированы варианты решений": "обмен на крипту, удержание фунтов, покупка активов."}, "Определены награды и влияние на личные финансы игроков."]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Банк Англии объявляет о коллапсе фунта, что запускает массовую панику и реструктуризацию экономики Лондона.\\n", "mechanics_links": [], "assets": []}, {"id": "crisis_flow", "title": "Ход событий", "body": "1. Узнать об угрозе обесценивания и подготовить активы.\\n2. Пережить банковскую панику и решить, что делать с капиталом.\\n3. Столкнуться с падением курса на 90% и последствиями выбранной стратегии.\\n4. Встроиться в экономику London Coin, используя новые правила.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "2 000 XP и финансовый результат от -90% до +50% в зависимости от решений. Квест меняет доступ к экономическим механикам Лондона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-pound-collapse"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Крах фунта» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

