-- Quest: canon-quest-detroit-abandoned-ruins
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-detroit-abandoned-ruins', 'Детройт 2020-2029 — Заброшенные руины', 'Игроки исследуют культовые руины Michigan Central Station и Packard Plant, сталкиваются с рисками и получают уникальную эстетическую награду.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-detroit-abandoned-ruins", "title": "Детройт 2020-2029 — Заброшенные руины", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-05T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["detroit", "urban-exploration", "post-apocalypse"], "topics": ["regional-history", "risk-play"], "related_systems": ["narrative-service", "exploration-system"], "related_documents": [{"id": "canon-quest-detroit-motor-city", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-340", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/340", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/detroit/2020-2029/quest-004-abandoned-ruins.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий «Заброшенные руины» находился в Markdown и не был доступен инструментам автоматизации.", "goal": "Оформить квест про urban exploration Детройта в структурированном формате знаний.", "essence": "Игроки исследуют культовые руины Michigan Central Station и Packard Plant, сталкиваются с рисками и получают уникальную эстетическую награду.", "key_points": ["Подчёркивается масштаб заброшенных площадей и субкультура urban explorers.", "Этапы квеста охватывают ключевые руины, угрозы и моральный выбор.", "Награды дают опыт, репутацию исследователя руин и соответствующее достижение."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Сценарий проводит игроков через заброшенные объекты Детройта, превращая индустриальные руины в площадку для атмосферного\\nисследования и сбора артефактов. На фоне постиндустриального упадка формируется обособленная культура исследователей.\\n", "mechanics_links": [], "assets": []}, {"id": "itinerary", "title": "Этапы маршрута", "body": "1. Michigan Central Station — осмотр 18-этажного заброшенного вокзала, встреча с граффити-артом и локальными эксплорерами.\\n2. Packard Automotive Plant — исследование 3,5 млн кв. футов руин, поиск артефактов и скрытых проходов.\\n3. Опасности — взаимодействие с криминалом, бездомными и угрозой обвалов, выбор стратегии проникновения.\\n4. Финал — решение, стоит ли продолжать ради редких находок или завершить миссию.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Отмечены 78 тысяч заброшенных зданий города, культовые локации Michigan Central Station и Packard Plant, а также риски,\\nсвязанные с криминалом и инфраструктурными обвалами. Подчёркивается эстетика постапокалипсиса и роль урбанистов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards_mechanics", "title": "Награды и механики", "body": "XP: 3000, Валюта: 0, Репутация «Urban Exploration»: +25. Игрок получает достижение «Исследователь Руин».\\nКлючевые механики — исследование заброшенных пространств, менеджмент рисков и визуальный сторителлинг города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 340, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-detroit-abandoned-ruins"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Заброшенные руины» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

