INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-tbilisi-2029-rose-revolution-legacy', 'Тбилиси 2020-2029 — Наследие революции роз', 'Игрок собирает свидетельства революции, знакомится с реформаторами и оппонентами, анализирует конфликт 2008 года и формирует собственную оценку.', 'main', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_revolution_archives", "text": "Посетить информационный центр в Тбилиси, изучить хронику событий, встретиться с организаторами протестов и найти оригинальные розы", "type": "investigate", "target": "rose_revolution_archives", "count": 1, "optional": false}, {"id": "learn_government_reforms", "text": "Встретиться с бывшими чиновниками, изучить реформу полиции, борьбу с коррупцией и модернизацию инфраструктуры", "type": "interact", "target": "government_reforms_meeting", "count": 1, "optional": false}, {"id": "meet_opposition", "text": "Разговаривать с оппозицией и гражданскими активистами, узнать о критике авторитарных методов и проблемах в регионах", "type": "diplomacy", "target": "opposition_dialogue", "count": 1, "optional": false}, {"id": "experience_2008_war", "text": "Пережить пятидневную войну через VR-сцены, узнать о потерях Абхазии и Южной Осетии, проанализировать последствия", "type": "interact", "target": "russian_georgian_war_vr", "count": 1, "optional": false}, {"id": "evaluate_legacy", "text": "Подготовить отчёт для международной миссии, взвешивая достижения и ошибки революции", "type": "research", "target": "revolution_legacy_evaluation", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "currency": 0, "items": [], "reputation": {"political_analyst": 15}, "flags": ["witness_of_change_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-tbilisi-2029-rose-revolution-legacy", "title": "Тбилиси 2020-2029 — Наследие революции роз", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tbilisi", "politics", "history"], "topics": ["democratic_transitions", "regional_conflicts"], "related_systems": ["narrative-service", "diplomacy-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-010-rose-revolution-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политической линии Грузии не хватало миссии, объясняющей влияние мирной революции роз и её последствий.", "goal": "Провести игрока через ключевых участников событий 2003 года, оценить реформы и их цену.", "essence": "Игрок собирает свидетельства революции, знакомится с реформаторами и оппонентами, анализирует конфликт 2008 года и формирует собственную оценку.", "key_points": ["Мирный характер протестов и символика роз.", "Резкие реформы — полиция, коррупция, модернизация.", {"Геополитические последствия": "обострение отношений с Россией и потеря контроля над регионами."}]}, "quest_definition": {"quest_type": "main", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_revolution_archives", "text": "Посетить информационный центр в Тбилиси, изучить хронику событий, встретиться с организаторами протестов и найти оригинальные розы", "type": "investigate", "target": "rose_revolution_archives", "count": 1, "optional": false}, {"id": "learn_government_reforms", "text": "Встретиться с бывшими чиновниками, изучить реформу полиции, борьбу с коррупцией и модернизацию инфраструктуры", "type": "interact", "target": "government_reforms_meeting", "count": 1, "optional": false}, {"id": "meet_opposition", "text": "Разговаривать с оппозицией и гражданскими активистами, узнать о критике авторитарных методов и проблемах в регионах", "type": "diplomacy", "target": "opposition_dialogue", "count": 1, "optional": false}, {"id": "experience_2008_war", "text": "Пережить пятидневную войну через VR-сцены, узнать о потерях Абхазии и Южной Осетии, проанализировать последствия", "type": "interact", "target": "russian_georgian_war_vr", "count": 1, "optional": false}, {"id": "evaluate_legacy", "text": "Подготовить отчёт для международной миссии, взвешивая достижения и ошибки революции", "type": "research", "target": "revolution_legacy_evaluation", "count": 1, "optional": false}], "rewards": {"experience": 5000, "currency": 0, "items": [], "reputation": {"political_analyst": 15}, "flags": ["witness_of_change_title"]}, "branches": []}, "content": {"sections": [{"id": "archives", "title": "Архивы революции", "body": "Игрок посещает информационный центр в Тбилиси, изучает хронику событий и встречается с организаторами мирных протестов. Находит оригинальные\\nрозы, которыми участники входили в парламент.\\n", "mechanics_links": [], "assets": []}, {"id": "reforms", "title": "Реформы нового правительства", "body": "Встреча с бывшими чиновниками демонстрирует реформу полиции, борьбу с коррупцией и модернизацию инфраструктуры. Игрок решает, какие\\nпроекты поддерживать.\\n", "mechanics_links": [], "assets": []}, {"id": "opposition", "title": "Тёмная сторона", "body": "Игрок разговаривает с оппозицией и гражданскими активистами, узнаёт о критике авторитарных методов, проблемах в регионах и ценности\\nбаланса власти.\\n", "mechanics_links": [], "assets": []}, {"id": "conflict", "title": "Русско-грузинская война 2008", "body": "С помощью документальных VR-сцен игрок переживает пятидневную войну, узнаёт о потерях Абхазии и Южной Осетии, анализирует последствия\\nреформ для безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "evaluation", "title": "Оценка наследия", "body": "В финале игрок подготавливает отчёт для международной миссии, взвешивая достижения и ошибки революции. Выбор влияет на дипломатические\\nсценарии и отношения с фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP, репутация «Политический аналитик» +15.\\n- Титул «Свидетель перемен» и доступ к дипломатическим заданиям в регионе.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 782, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики демократических переходов и региональных конфликтов."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Наследие революции роз» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

