INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vladivostok-2029-golden-bridge', 'Владивосток 2020-2029 — Золотой мост', 'Игрок поднимается на мост, изучает инженерные особенности, делает фото панорамы и возвращается ночью ради подсветки.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "approach_bridge", "text": "Подойти к мосту у смотровой площадки, узнать историю строительства и получить гид по безопасному переходу", "type": "explore", "target": "golden_bridge_viewpoint", "count": 1, "optional": false}, {"id": "cross_bridge", "text": "Перейти через мост, используя режим фотосъёмки и изучая вантовую систему", "type": "perform", "target": "golden_bridge_crossing", "count": 1, "optional": false}, {"id": "capture_panorama", "text": "Создать AR-панорамы бухты Золотой Рог с середины моста, узнать о портах и судостроении", "type": "interact", "target": "golden_bridge_panorama_point", "count": 1, "optional": false}, {"id": "return_at_night", "text": "Вернуться после заката, чтобы увидеть подсветку моста и снять короткое видео", "type": "perform", "target": "golden_bridge_night_lighting", "count": 1, "optional": false}]'::jsonb, '{"experience": 800, "currency": 0, "items": [], "reputation": {"vladivostok_tourism": 10}, "flags": ["golden_bridge_conqueror_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vladivostok-2029-golden-bridge", "title": "Владивосток 2020-2029 — Золотой мост", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vladivostok", "architecture", "tourism"], "topics": ["urban_landmarks", "sightseeing"], "related_systems": ["narrative-service", "exploration-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/vladivostok/2020-2029/quest-001-golden-bridge.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристической линии Владивостока требуется знаковая прогулка по визитной карточке города — Золотому мосту.", "goal": "Познакомить игрока с архитектурой моста, видом на бухту Золотой Рог и ночной подсветкой.", "essence": "Игрок поднимается на мост, изучает инженерные особенности, делает фото панорамы и возвращается ночью ради подсветки.", "key_points": ["Вантовая конструкция длиной 1 388 м.", "Площадки для фото и социального контента.", "Ночная подсветка и история строительства к саммиту АТЭС."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "approach_bridge", "text": "Подойти к мосту у смотровой площадки, узнать историю строительства и получить гид по безопасному переходу", "type": "explore", "target": "golden_bridge_viewpoint", "count": 1, "optional": false}, {"id": "cross_bridge", "text": "Перейти через мост, используя режим фотосъёмки и изучая вантовую систему", "type": "perform", "target": "golden_bridge_crossing", "count": 1, "optional": false}, {"id": "capture_panorama", "text": "Создать AR-панорамы бухты Золотой Рог с середины моста, узнать о портах и судостроении", "type": "interact", "target": "golden_bridge_panorama_point", "count": 1, "optional": false}, {"id": "return_at_night", "text": "Вернуться после заката, чтобы увидеть подсветку моста и снять короткое видео", "type": "perform", "target": "golden_bridge_night_lighting", "count": 1, "optional": false}], "rewards": {"experience": 800, "currency": 0, "items": [], "reputation": {"vladivostok_tourism": 10}, "flags": ["golden_bridge_conqueror_title"]}, "branches": []}, "content": {"sections": [{"id": "approach", "title": "Подход к мосту", "body": "Игрок стартует у смотровой площадки, узнаёт историю строительства и получает гид по безопасному переходу.\\n", "mechanics_links": [], "assets": []}, {"id": "crossing", "title": "Переход через мост", "body": "Во время перехода доступен режим фотосъёмки, информация о вантовой системе и мини-игра по удержанию равновесия при ветре.\\n", "mechanics_links": [], "assets": []}, {"id": "panorama", "title": "Панорамы Золотого Рога", "body": "На середине моста расположена обзорная точка; игрок создает AR-панорамы бухты и узнаёт о портах и судостроении.\\n", "mechanics_links": [], "assets": []}, {"id": "night_return", "title": "Ночной возврат", "body": "Игрок возвращается после заката, чтобы увидеть подсветку моста и снять короткое видео. Награды зависят от качества контента.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, эстетика +10.\\n- Титул «Покоритель Золотого моста» и бонусы к социальным сетям города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 771, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики туризма и архитектурных достопримечательностей."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Золотой мост» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO 