INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vladivostok-2029-king-crab-feast', 'Владивосток 2020-2029 — «Пир камчатского краба»', 'Герой бронирует ресторан, наблюдает приготовление и получает элитный бафф после пира.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "book_restaurant", "text": "Забронировать столик в престижном ресторане на набережной, выбрать живого краба из аквариума и подтвердить меню", "type": "interact", "target": "premium_seafood_restaurant", "count": 1, "optional": false}, {"id": "watch_preparation", "text": "Наблюдать процесс приготовления: очистка, варка на пару, сервировка, участвовать в мини-игре по подбору соусов", "type": "interact", "target": "crab_preparation_station", "count": 1, "optional": false}, {"id": "carve_crab", "text": "Аккуратно извлечь мясо из клешней и панциря, используя специальные ножницы и щипцы, минимизируя потери", "type": "craft", "target": "crab_carving_station", "count": 1, "optional": false}, {"id": "taste_with_sommelier", "text": "Продегустировать краба с сомелье по морепродуктам, выбрать напитки для усиления баффа", "type": "consume", "target": "seafood_tasting_table", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "currency": -200, "items": [{"id": "king_of_sea_buff", "name": "Бафф «Король моря»: +40% HP на 12 часов", "quantity": 1}, {"id": "crab_conqueror_achievement", "name": "Ачивка «Покоритель краба»", "quantity": 1}], "reputation": {}, "flags": ["crab_conqueror_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vladivostok-2029-king-crab-feast", "title": "Владивосток 2020-2029 — «Пир камчатского краба»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "c