', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-tube-races", "title": "Лондон 2030-2039 — Гонки в метро", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "racing", "underground"], "topics": ["events", "mobility"], "related_systems": ["narrative-service", "racing-system", "event-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2030-2039/quest-015-tube-races.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Событие гонок по лондонскому метро было описано в Markdown и не было доступно в знаниевой базе.", "goal": "Задокументировать ховербайковые гонки по туннелям Underground в формате YAML.", "essence": "Игрок регистрируется на нелегальные гонки по Circle Line, тюнингует ховербайк и соревнуется за легендарный приз.\\n", "key_points": ["Описаны этапы регистрации, модификации байка и прохождения трёх кругов по Circle Line.", {"Указаны уникальные особенности трассы": "объявления «Mind the Gap», ремонтные дроны и движущиеся поезда."}, "Определены награды, включая редкий ховербайк и репутацию гонщиков."]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Лондонское метро становится ареной высокоскоростных ховербайк-гонок с неоновой эстетикой и классическими объявлениями.\\n", "mechanics_links": [], "assets": []}, {"id": "event_flow", "title": "Ход события", "body": "1. Пройти регистрацию на станции Piccadilly Circus и получить слот в заезде.\\n2. Настроить ховербайк, подбирая улучшения под туннельные условия.\\n3. Пройти три круга по Circle Line, избегая поездов и дронов.\\n4. Финишировать на станции King''s Cross и получить место в рейтинге.\\n", "mechanics_links": [], "assets": []}, {"id": "track_features", "title": "Особенности трассы", "body": "Круговая трасса, голосовые объявления «Mind the Gap», динамические препятствия и визуальные эффекты туннелей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Победитель получает 15 000 едди и легендарный ховербайк «Underground Racer», участники — денежные призы и рост репутации гонщиков.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-tube-races"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Гонки в метро» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

