INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-lon-underground-network', 'Лондон 2040-2060 — Подземная сеть', 'Игрок помогает хакерам устанавливать серверы на заброшенных станциях, прокладывает кабели и отражает рейд NetWatch, получая доступ к уникальной подземной сети.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-underground-network", "title": "Лондон 2040-2060 — Подземная сеть", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "hacking", "metro"], "topics": ["faction", "infrastructure"], "related_systems": ["narrative-service", "hacking-system", "network-infrastructure"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2040-2060/quest-022-underground-network.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест создания локальной сети в туннелях метро существовал лишь в Markdown и не был описан по шаблону знаний.", "goal": "Структурировать фракционную миссию по развёртыванию подпольного NET в Лондоне.", "essence": "Игрок помогает хакерам устанавливать серверы на заброшенных станциях, прокладывает кабели и отражает рейд NetWatch, получая доступ к уникальной подземной сети.\\n", "key_points": ["Проработаны этапы развёртывания сети и защита инфраструктуры.", {"Указаны ключевые локации": "Aldwych, Down Street и викторианские туннели."}, {"Определены награды": "доступ к сети, бесплатный интернет в метро и денежная выплата."}]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "После фрагментации глобального NET хакеры Лондона строят автономную сеть в туннелях The Tube.\\n", "mechanics_links": [], "assets": []}, {"id": "mission_flow", "title": "Ход квеста", "body": "1. Изучить карту туннелей и определить подходящие заброшенные станции.\\n2. Установить серверы на пяти точках, обеспечив питание и охлаждение.\\n3. Проложить кабели по старым туннелям, избегая ловушек и обрушений.\\n4. Активировать сеть и протестировать связь между узлами.\\n5. Отразить рейд NetWatch, защищая инфраструктуру.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "2 000 XP, 5 000 едди и постоянный доступ к подземной сети, предоставляющий быстрые маршруты и бесплатный интернет в метро.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-underground-network"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Подземная сеть» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

