INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-ararat-view', 'Ереван 2020-2029 — Вид на Арарат', 'Игрок поднимается на Каскад, наблюдает панораму, слушает истории жителей и завершает квест личным посвящением Арарату.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-ararat-view", "title": "Ереван 2020-2029 — Вид на Арарат", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "ararat", "heritage"], "topics": ["national_identity", "emotional_landscape"], "related_systems": ["narrative-service", "ambience-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-001-ararat-view.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линии Еревана требовался эмоциональный сценарий, фиксирующий символичность горы Арарат для армян.", "goal": "Провести игрока к точке обзора на Каскаде, показать контраст доступного вида и недосягаемости священной горы.", "essence": "Игрок поднимается на Каскад, наблюдает панораму, слушает истории жителей и завершает квест личным посвящением Арарату.", "key_points": ["Двуглавая гора видима каждый день, хоть и находится за границей.", "Каскад служит как музей под открытым небом и место встреч.", "Игрок фиксирует эмоции в виде фотодневника и послания будущему."]}, "content": {"sections": [{"id": "ascent", "title": "Подъём на Каскад", "body": "Игрок преодолевает лестницу Каскада, знакомится с фонтанами и арт-объектами, готовясь к встрече с панорамой.\\n", "mechanics_links": [], "assets": []}, {"id": "viewpoint", "title": "Смотровая площадка", "body": "На вершине открывается вид на Ереван и Арарат. Игрок активирует AR-гид, узнаёт о Малой и Большой вершинах.\\n", "mechanics_links": [], "assets": []}, {"id": "stories", "title": "Голоса города", "body": "Встречи с москвичами, художниками и старожилами. Игрок записывает их истории о символе и личных воспоминаниях.\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Личное посвящение", "body": "В финале игрок создаёт фотодневник и пишет послание о значении Арарата. Это формирует бонус к культурной репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, эмоции «Гордость и меланхолия».\\n- Титул «Взгляд на Арарат».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 753, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Вид на Арарат» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

