INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-genocide-memorial', 'Ереван 2020-2029 — Мемориал геноцида', 'Игрок поднимается к мемориалу, изучает экспозиции, беседует с хранителями памяти и завершает миссию минутой молчания у вечного огня.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-genocide-memorial", "title": "Ереван 2020-2029 — Мемориал геноцида", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "genocide-memory", "memorial"], "topics": ["historical_trauma", "remembrance"], "related_systems": ["narrative-service", "education-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-002-genocide-memorial.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Мемориальной ветке Армении требовался сценарий, аккуратно раскрывающий трагедию 1915 года.", "goal": "Провести игрока через Цицернакаберд и музей геноцида, углубить понимание истории и международного признания.", "essence": "Игрок поднимается к мемориалу, изучает экспозиции, беседует с хранителями памяти и завершает миссию минутой молчания у вечного огня.", "key_points": ["12 плит мемориала символизируют утраченные провинции.", "Экспозиция музея сочетает документы, фото и AR-реконструкции.", "Финал подчёркивает обещание помнить и рассказывать миру."]}, "content": {"sections": [{"id": "ascent", "title": "Подъём к мемориалу", "body": "Игрок поднимается на холм Цицернакаберд, слушая аудиосопровождение о событиях 1915 года и строении мемориала.\\n", "mechanics_links": [], "assets": []}, {"id": "museum", "title": "Экспозиция музея", "body": "Внутри музея игрок изучает документы, фотографии и личные истории. AR-сцены демонстрируют хронику депортаций.\\n", "mechanics_links": [], "assets": []}, {"id": "eternal_flame", "title": "Вечный огонь", "body": "Игрок приближается к вечному огню и оставляет цветы. Взаимодействие фиксирует эмоциональный ответ персонажа.\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Обет памяти", "body": "Финальная сцена — минута молчания и запись обещания рассказать об увиденном. Игрок получает допуск к международным кампаниям памяти.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP, эмоция «Скорбь и память».\\n- Титул «Мы помним» и доступ к миссиям дипломатического признания.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 754, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Мемориал геноцида» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

