INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-armenian-cognac', 'Ереван 2020-2029 — Армянский коньяк', 'Игрок посещает погреба, дегустирует линейку коньяков, узнаёт историю Черчилля и формирует персональную карту ароматов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-armenian-cognac", "title": "Ереван 2020-2029 — Армянский коньяк", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "cognac", "gastronomy"], "topics": ["cultural_pride", "craft_traditions"], "related_systems": ["narrative-service", "wellbeing-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-003-armenian-cognac.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гастрономической линии Армении требовался сценарий, раскрывающий легенду коньяка «Арарат» и его мировую славу.", "goal": "Провести игрока по заводу, показать мастерство выдержки и позволить ощутить влияние армянского бренда на мировую историю.", "essence": "Игрок посещает погреба, дегустирует линейку коньяков, узнаёт историю Черчилля и формирует персональную карту ароматов.", "key_points": ["Завод «Арарат» работает с 1887 года и хранит столетние бочки.", "Разные звёзды линейки рассказывают о выдержке и вкусе.", "Игрок получает шанс собрать эксклюзивную купажную подборку."]}, "content": {"sections": [{"id": "tour", "title": "Экскурсия по заводу", "body": "Гид знакомит с историей бренда, показывая линии розлива и коллекционные бочки. Игрок узнаёт о союзах компании с Черчиллем.\\n", "mechanics_links": [], "assets": []}, {"id": "cellars", "title": "Погреба выдержки", "body": "В подземных хранилищах игрок исследует дубовые бочки, изучает процессы выдержки и записывает ароматические заметки.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "Последовательно пробуются «3 звезды», «5 звёзд» и «Наири». Игрок фиксирует вкусовые профили и обсуждает легенды с мастером купажа.\\n", "mechanics_links": [], "assets": []}, {"id": "blend", "title": "Собственный купаж", "body": "Финальная мини-игра предлагает смешать ароматы, создавая личный сувенир. Результат влияет на будущие скидки и мероприятия.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP, эффект «Харизма» +30% на 12 часов, расход 100 еддис.\\n- Титул «Коньячный знаток» и доступ к дегустационному клубу.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 755, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Армянский коньяк» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

