INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-khor-virap-monastery', 'Ереван 2020-2029 — Монастырь Хор Вирап', 'Игрок приезжает к монастырю, спускается в яму Григория, слушает историю принятия христианства и завершает миссию панорамой горы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-khor-virap-monastery", "title": "Ереван 2020-2029 — Монастырь Хор Вирап", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "pilgrimage", "christianity"], "topics": ["sacred_sites", "historical_conversion"], "related_systems": ["narrative-service", "exploration-service", "liveops-service"], "related_documents": [{"id": "github-issue-756", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/756", "relation": "migrated_to", "migrated_at": "2025-11-23T00:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-004-khor-virap-monastery.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Религиозной линии Армении нужен квест о месте, где родилась христианская идентичность страны.", "goal": "Провести игрока в Хор Вирап, показать историю Григория Просветителя и вид на Арарат.", "essence": "Игрок приезжает к монастырю, спускается в яму Григория, слушает историю принятия христианства и завершает миссию панорамой горы.", "key_points": ["Яма Григория — символ духовной стойкости.", "Монастырь стоит у подножия Арарата.", "Квест связывает религиозное наследие с современным паломничеством."]}, "content": {"sections": [{"id": "journey", "title": "Дорога к монастырю", "body": "Игрок преодолевает 30 км от Еревана, знакомится с историей комплекса и получает задания по взаимодействию с паломниками.\\n", "mechanics_links": [], "assets": []}, {"id": "descent", "title": "Спуск в яму", "body": "Спуск в шестиметровую яму, где Григорий Просветитель провёл 13 лет. Игрок использует фонарик и записывает впечатления.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "Принятие христианства", "body": "Настоятель рассказывает о 301 годе и превращении Армении в первую христианскую страну. Игрок участвует в интерактивной хронике.\\n", "mechanics_links": [], "assets": []}, {"id": "panorama", "title": "Вид на Арарат", "body": "Финал — панорама с монастыря на гору Арарат. Игрок делает снимок и делится им, получая бонусы к культурной репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 500 XP, культура +20.\\n- Титул «Паломник Хор Вирапа».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 756, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Монастырь Хор Вирап» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIME