INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-khachkars-art', 'Ереван 2020-2029 — Искусство хачкаров', 'Игрок исследует галерею из каменных крестов, расшифровывает узоры памяти и создаёт собственный элемент с помощью мастера.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-khachkars-art", "title": "Ереван 2020-2029 — Искусство хачкаров", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "heritage", "unesco"], "topics": ["cultural_artisanry", "memorial_traditions"], "related_systems": ["narrative-service", "exploration-service", "progression-service"], "related_documents": [{"id": "github-issue-757", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/757", "relation": "migrated_to", "migrated_at": "2025-11-23T00:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-005-khachkars-art.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурной линии Армении требовалась миссия, раскрывающая сакральное искусство хачкаров и их статус наследия UNESCO.", "goal": "Погрузить игрока в парк хачкаров, показать символику резьбы и дать попробовать себя в мастерской камнереза.", "essence": "Игрок исследует галерею из каменных крестов, расшифровывает узоры памяти и создаёт собственный элемент с помощью мастера.", "key_points": ["Подчёркивает уникальность более 50 000 хачкаров.", "Включает интерактивное обучение символам и технике резьбы.", "Раскрывает связь искусства с семейной памятью и духовными практиками."]}, "content": {"sections": [{"id": "gallery", "title": "Парк хачкаров", "body": "Игрок прибывает в музей под открытым небом, проходит экскурсию по памятным хачкарам и использует AR-метки для расшифровки символов.\\n", "mechanics_links": [], "assets": []}, {"id": "symbolism", "title": "Символика резьбы", "body": "Гид объясняет значение креста, растительных узоров и геометрии. Мини-игра по сопоставлению узоров помогает закрепить знания.\\n", "mechanics_links": [], "assets": []}, {"id": "workshop", "title": "Мастерская камнереза", "body": "Игрок надевает защиту, повторяет шаги мастера и вырезает небольшой фрагмент орнамента, отслеживая точность и аккуратность.\\n", "mechanics_links": [], "assets": []}, {"id": "dedication", "title": "Память и посвящение", "body": "Завершающий ритуал — размещение мини-хачкара в стенде памяти и запись послания для семьи или гильдии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, культура +15.\\n- Титул «Мастер хачкаров» и доступ к ремесленным контрактам по резьбе.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 757, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Искусство хачкаров» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branch