INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-lon-thames-pirates', 'Лондон 2040-2060 — Пираты Темзы', 'Игрок встречается с капитаном пиратов на плавучем баре, выполняет испытание по ограблению корп-баржи, участвует в морском бою и выбирает роль в гильдии.
