INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-lavash-baking', 'Ереван 2020-2029 — Выпечка лаваша', 'Игрок присоединяется к пекарям, месит тесто, выпекает лаваш за секунды и участвует в ритуале благословения хлеба.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-lavash-baking", "title": "Ереван 2020-2029 — Выпечка лаваша", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "bread", "unesco"], "topics": ["culinary_craft", "ritual_traditions"], "related_systems": ["narrative-service", "progression-service", "liveops-service"], "related_documents": [{"id": "github-issue-759", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/759", "relation": "migrated_to", "migrated_at": "2025-11-23T00:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-007-lavash-baking.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Для кулинарной ветки требовалась миссия о выпечке армянского лаваша — нематериального наследия UNESCO.", "goal": "Дать игроку опыт работы в тонире и показать роль лаваша в семейных и свадебных традициях.", "essence": "Игрок присоединяется к пекарям, месит тесто, выпекает лаваш за секунды и участвует в ритуале благословения хлеба.", "key_points": ["Демонстрирует технологию выпекания в тонире.", "Включает мини-игру на скорость и точность.", "Подчеркивает культурное значение хлеба для семейных обрядов."]}, "content": {"sections": [{"id": "bakery", "title": "Пекарня с тониром", "body": "Игрок приходит в традиционную пекарню, знакомится с командой пекарей и готовится к мастер-классу.\\n", "mechanics_links": [], "assets": []}, {"id": "dough", "title": "Замес теста", "body": "Механика по поддержанию правильных пропорций муки, воды и соли влияет на эластичность заготовки.\\n", "mechanics_links": [], "assets": []}, {"id": "baking", "title": "Выпечка в тонире", "body": "Игрок раскатывает заготовку, прилепляет её к стенке печи и вовремя снимает, удерживая лаваш целым. Таймер задаёт ритм.\\n", "mechanics_links": [], "assets": []}, {"id": "tradition", "title": "Традиции хлеба", "body": "После выпечки игрок участвует в свадебном ритуале с лавашем, узнаёт о его роли в благословениях и семейных ужинах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, навык «Выпечка» +10.\\n- Титул «Мастер лаваша», бафф «Домашнее тепло» и доступ к кулинарным контрактам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 759, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Выпечка лаваша» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

