INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-duduk-music', 'Ереван 2020-2029 — Музыка дудука', 'Игрок посещает музыкальный салон, общается с мастерами, слушает репертуар и пробует инструмент из дерева абрикоса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-duduk-music", "title": "Ереван 2020-2029 — Музыка дудука", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "music", "unesco"], "topics": ["cultural_performance", "emotional_experience"], "related_systems": ["narrative-service", "liveops-service", "progression-service"], "related_documents": [{"id": "github-issue-760", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/760", "relation": "migrated_to", "migrated_at": "2025-11-23T00:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-008-duduk-music.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало эмоционального культурного события, посвящённого дудуку — символу армянской музыки, признанному UNESCO.", "goal": "Организовать камерный концерт, дать игроку почувствовать звучание дудука и попробовать сыграть собственную мелодию.", "essence": "Игрок посещает музыкальный салон, общается с мастерами, слушает репертуар и пробует инструмент из дерева абрикоса.", "key_points": ["Камерная обстановка усиливает эмоциональное восприятие.", "Включает демонстрацию традиционных и современных композиций.", "Завершение — покупка дудука и обучение базовым приёмам игры."]}, "content": {"sections": [{"id": "salon", "title": "Музыкальный салон", "body": "Игрок прибывает в зал, знакомится с музыкантами и узнаёт историю дудука как «голоса Армении».\\n", "mechanics_links": [], "assets": []}, {"id": "performance", "title": "Концерт традиций", "body": "Дуэт мелодии и дрона исполняет классические мелодии, современные обработки и фрагмент из «Gladiator». Игрок управляет режимами прослушивания.\\n", "mechanics_links": [], "assets": []}, {"id": "instrument", "title": "Знакомство с инструментом", "body": "Игрок наблюдает производство дудука из абрикосового дерева, изучает трости и настраивает собственный инструмент.\\n", "mechanics_links": [], "assets": []}, {"id": "lesson", "title": "Первые ноты", "body": "Мини-игра по удержанию дыхания и нот позволяет сыграть простую мелодию. Результат влияет на репутацию музыкантов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP, культура +15, -50 еддис на покупку дудука.\\n- Титул «Слушатель дудука» и доступ к музыкальным событиям liveops.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 760, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Музыка дудука» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, con