INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-yerevan-2029-velvet-revolution', 'Ереван 2020-2029 — Бархатная революция 2018', 'Игрок изучает хронику событий, присутствует на площади Республики, разговаривает с активистами и делает выводы для международной миссии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-yerevan-2029-velvet-revolution", "title": "Ереван 2020-2029 — Бархатная революция 2018", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:27:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["yerevan", "politics", "revolution"], "topics": ["civic_movements", "democratic_reforms"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "progression-service"], "related_documents": [{"id": "github-issue-761", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/761", "relation": "migrated_to", "migrated_at": "2025-11-23T00:27:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/yerevan/2020-2029/quest-009-velvet-revolution.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политической ветке Армении нужен был квест, анализирующий последствия Бархатной революции 2018 года.", "goal": "Проследить ключевые этапы мирных протестов, встретиться с участниками и оценить реформы нового правительства.", "essence": "Игрок изучает хронику событий, присутствует на площади Республики, разговаривает с активистами и делает выводы для международной миссии.", "key_points": ["Мирный характер протестов и роль Николы Пашиняна.", "Реформы в коррупции, демократии и экономике.", "Выбор игрока влияет на дипломатические и внутренние сценарии."]}, "content": {"sections": [{"id": "archives", "title": "Хроника революции", "body": "Игрок посещает новостной центр, просматривает документальные записи и собирает основные факты апрельских событий 2018 года.\\n", "mechanics_links": [], "assets": []}, {"id": "square", "title": "Площадь Республики", "body": "Через реконструкцию игрок оказывается в эпицентре протестов, взаимодействует с NPC и помогает организаторам поддерживать мирный порядок.\\n", "mechanics_links": [], "assets": []}, {"id": "reforms", "title": "Новое правительство", "body": "Встречи с чиновниками «Мой шаг» раскрывают планы по борьбе с коррупцией, демократизации и экономическим реформам. Игрок фиксирует результаты.\\n", "mechanics_links": [], "assets": []}, {"id": "opposition", "title": "Голоса критиков", "body": "Оппоненты рассказывают о проблемах, рисках поляризации и нереализованных ожиданиях. Игрок взвешивает аргументы обеих сторон.\\n", "mechanics_links": [], "assets": []}, {"id": "evaluation", "title": "Оценка изменений", "body": "Финальный отчёт требует выбрать позицию: поддержка реформ, критический отчёт или предложение гибридного пути. Выбор влияет на репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP, эмоция «Надежда».\\n- Титул «Свидетель революции» и доступ к дипломатическим линиям Кавказа.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 761, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Бархатная революция 2018» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

