INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-amsterdam-2020-2029-canal-cruise', 'Амстердам 2020-2029 — Круиз по каналам', 'Игрок отправляется в круиз, изучает архитектуру XVII века и собирает фото-награды.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "rent_boat", "text": "Арендовать лодку или круизный тур на каналах Амстердама. Выбрать тип круиза (частная лодка или тур с гидом), время (дневной/вечерний) и оплатить 25 еддис", "type": "interact", "target": "amsterdam_canal_boat_rental", "count": 1, "optional": false}, {"id": "explore_canals", "text": "Пройти по трем знаковым каналам (Herengracht, Prinsengracht, Keizersgracht), активировать AR-метки о торговых домах XVII века и изучить историю концентрических колец города", "type": "travel", "target": "amsterdam_canals_route", "count": 3, "optional": false}, {"id": "study_architecture", "text": "Изучить налоговую систему по ширине фасада, сфотографировать плавучие дома, велосипедные мосты и узкие фасады. Снимки пополняют коллекцию туристического журнала", "type": "perform", "target": "amsterdam_architecture_photos", "count": 5, "optional": false}, {"id": "evening_cruise", "text": "Принять участие в вечернем круизе с подсветкой мостов, активировать liveops-событие «Ночная Венеция» и получить бонусы за совместные эмоции с группой", "type": "perform", "target": "amsterdam_evening_cruise", "count": 1, "optional": false}]'::jsonb, '{"experience": 800, "currency": -25, "items": [], "reputation": {"amsterdam_tourism": 15}, "flags": ["canal_cruise_completed", "amsterdam_photographer_unlocked", "amsterdam_historian_unlocked", "amsterdam_tourist_unlocked"]}'::jsonb, '[{"id": "photographer_path", "unlock_condition": {"type": "objective_completion", "objective_id": "study_architecture", "min_count": 5}, "consequences": {"reputation": {"amsterdam_tourism": 5}, "flags": ["photographer_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "photography", "location": "amsterdam"}]}}, {"id": "historian_path", "unlock_condition": {"type": "skill_check", "skill": "history", "min_value": 0.6}, "consequences": {"reputation": {"amsterdam_tourism": 5}, "flags": ["historian_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "historical", "location": "amsterdam"}]}}, {"id": "tourist_path", "unlock_condition": {"type": "quest_completion", "quest_id": "quest-amsterdam-2020-2029-canal-cruise"}, "consequences": {"reputation": {"amsterdam_tourism": 10}, "flags": ["tourist_events_unlocked"], "unlocked_content": [{"event_type": "tourism", "location": "amsterdam"}]}}]'::jsonb, '{"metadata": {"id": "quest-amsterdam-2020-2029-canal-cruise", "title": "Амстердам 2020-2029 — Круиз по каналам", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["amsterdam", "canals", "tourism"], "topics": ["urban_sightseeing", "heritage_experience"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "exploration-service", "liveops-service", "tourism-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-001-canal-cruise.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристической линии Амстердама не хватало легкого атмосферного маршрута по историческим каналам.", "goal": "Позволить и