nlocked"]}, "branches": [{"id": "photographer_path", "unlock_condition": {"type": "objective_completion", "objective_id": "study_architecture", "min_count": 5}, "consequences": {"reputation": {"amsterdam_tourism": 5}, "flags": ["photographer_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "photography", "location": "amsterdam"}]}}, {"id": "historian_path", "unlock_condition": {"type": "skill_check", "skill": "history", "min_value": 0.6}, "consequences": {"reputation": {"amsterdam_tourism": 5}, "flags": ["historian_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "historical", "location": "amsterdam"}]}}, {"id": "tourist_path", "unlock_condition": {"type": "quest_completion", "quest_id": "quest-amsterdam-2020-2029-canal-cruise"}, "consequences": {"reputation": {"amsterdam_tourism": 10}, "flags": ["tourist_events_unlocked"], "unlocked_content": [{"event_type": "tourism", "location": "amsterdam"}]}}]}, "content": {"sections": [{"id": "booking", "title": "Аренда лодки", "body": "Игрок выбирает частную лодку или круизный тур. От выбора зависит сопровождение гида, стоимость и количество остановок по пути.\\n", "mechanics_links": [], "assets": []}, {"id": "main_route", "title": "Маршрут по Herengracht, Prinsengracht и Keizersgracht", "body": "Путешествие проходит по трем знаковым каналам. Игрок активирует AR-метки о торговых домах XVII века и узнает, как формировались\\nконцентрические кольца города.\\n", "mechanics_links": [], "assets": []}, {"id": "architecture", "title": "Узкие дома и мосты", "body": "Остановка для изучения налоговой системы по ширине фасада, фотографирование плавучих домов и велосипедных мостов. Снимки пополняют\\nколлекцию туристического журнала.\\n", "mechanics_links": [], "assets": []}, {"id": "evening_mode", "title": "Вечерняя подсветка", "body": "Программа для вечернего круиза с подсветкой мостов. Игрок активирует liveops-событие «Ночная Венеция» и получает бонусы за\\nсовместные эмоции с группой.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP и эстетический рейтинг +15.\\n- Стоимость 25 еддис, окупаемая социальными бонусами и фото-наградами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 743, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches согласно архитектуре системы квестов. Детализированы цели квеста, награды и ветвления на основе концепции от Idea Writer."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Круиз по каналам» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

