', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-last-gentleman", "title": "Лондон 2040-2060 — Последний джентльмен", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "etiquette", "social"], "topics": ["culture", "progression"], "related_systems": ["narrative-service", "social-system", "reputation-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2040-2060/quest-030-last-gentleman.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о последнем джентльмене был сохранён в Markdown и не попадал в базу знаний.", "goal": "Структурировать социальное приключение, знакомящее игроков с британскими традициями.", "essence": "Игрок посещает клуб джентльменов, обучается этикету, шьёт костюм на Savile Row, посещает оперу и получает титул почётного джентльмена.\\n", "key_points": ["Описаны этапы обучения и культурные испытания.", {"Зафиксированы региональные детали": "цилиндр, трость, чай в 17:00 и формальные обращения."}, {"Определены награды": "опыт, социальный титул и бонус к переговорам."}]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Столкновение с «последним джентльменом» погружает героя в викторианские традиции в сердце киберпанковского Лондона.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Ход квеста", "body": "1. Познакомиться со стариком в клубе джентльменов и пройти вводные испытания вежливости.\\n2. Пройти мини-игру по этикету, выучив правила поведения и обращения.\\n3. Сшить костюм на Savile Row и подобрать аксессуары.\\n4. Посетить оперу в Ковент-Гардене, демонстрируя новые манеры.\\n5. Получить титул почётного джентльмена и доступ к элитным клубам.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "800 XP, титул «Джентльмен», бонус +5 к переговорам с элитой и уникальные социальные связи.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-last-gentleman"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Последний джентльмен» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

