INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-amsterdam-2020-2029-red-light-district', 'Амстердам 2020-2029 — Квартал красных фонарей', 'Игрок знакомится с работницами, туристами и муниципальными инспекторами, влияя на баланс интересов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-amsterdam-2020-2029-red-light-district", "title": "Амстердам 2020-2029 — Квартал красных фонарей", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["amsterdam", "red-light", "faction"], "topics": ["regulated_economy", "social_dynamics"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "faction-service", "reputation-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-004-red-light-district.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало квеста, раскрывающего специфику легализованной секс-индустрии Амстердама и ее социальные последствия.", "goal": "Провести игрока по вечернему маршруту De Wallen, показать работу витрин и систему регулирования.", "essence": "Игрок знакомится с работницами, туристами и муниципальными инспекторами, влияя на баланс интересов.", "key_points": ["Демонстрирует легализацию 2000 года и правила налогового надзора.", "Позволяет игроку занять позицию между фракциями секс-индустрии и муниципалитета.", "Создает точку входа в социальные мини-игры и события безопасности."]}, "content": {"sections": [{"id": "arrival", "title": "Вечерний вход в De Wallen", "body": "Игрок прибывает в район после заката, наблюдает подсветку и туристический поток. Вступительная сцена задает правила поведения и\\nвлияние камер.\\n", "mechanics_links": [], "assets": []}, {"id": "windows", "title": "Витрины и диалоги", "body": "Взаимодействие с работницами через витрины, обсуждение условий труда и личной безопасности. Выборы влияют на отношение фракции.\\n", "mechanics_links": [], "assets": []}, {"id": "regulation", "title": "Инспекция и налоги", "body": "Муниципальные инспекторы проводят проверку. Игрок помогает уладить бюрократию или сообщает о нарушениях, влияя на экономику района.\\n", "mechanics_links": [], "assets": []}, {"id": "crowd", "title": "Поток туристов", "body": "Игрок управляет толпой, предотвращает съемку запрещенных зон и поддерживает репутацию района, получая социальные бонусы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, социальный рейтинг +10.\\n- Доступ к ветке фракции De Wallen и событиям безопасности.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 746, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Квартал красных фонарей» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

