INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-detroit-1967-riots', 'Детройт 2020-2029 — Беспорядки 1967 года', 'Игрок исследует ключевые локации 12th Street Riot, анализирует причины конфликта и последствия «white flight» для города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-detroit-1967-riots", "title": "Детройт 2020-2029 — Беспорядки 1967 года", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-05T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["detroit", "social-conflict", "history"], "topics": ["civil-rights", "urban-history"], "related_systems": ["narrative-service", "investigation-system"], "related_documents": [{"id": "canon-lore-timeline-2060-2077", "relation": "references"}, {"id": "canon-quest-detroit-8-mile-road", "relation": "parallels"}, {"id": "github-issue-341", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/341", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/detroit/2020-2029/quest-007-1967-riots.md", "visibility": "internal", "audience": ["narrative", "concept", "live-ops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исторический квест о беспорядках 1967 года существовал только в виде Markdown и не интегрировался в процессы Vision.", "goal": "Структурировать сценарий «Беспорядки 1967 года» для проработки социально-исторической линии Детройта.", "essence": "Игрок исследует ключевые локации 12th Street Riot, анализирует причины конфликта и последствия «white flight» для города.", "key_points": ["Подчёркнуты масштабы трагедии, участие национальной гвардии и глубина расовых противоречий.", "Этапы квеста включают историческое расследование, работу с архивными материалами и социальные выборы.", "Награды дают крупный опыт, историческую репутацию и достижение «Свидетель Беспорядков»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест раскрывает одну из самых трагичных страниц истории Детройта. Игрок исследует причины и последствия беспорядков,\\nпосещает ключевые места и собирает свидетельства, чтобы понять неизлечённые раны города.\\n", "mechanics_links": [], "assets": []}, {"id": "investigation", "title": "Этапы расследования", "body": "1. Изучение рейда 23 июля 1967 года и цепочки событий, приведших к эскалации.\\n2. Посещение Rosa Parks Boulevard (бывшей 12th Street) и других горячих точек.\\n3. Встреча с NPC-свидетелями, сбор архивов и фото.\\n4. Анализ последствий: «white flight», экономический спад, рост пустующих кварталов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Фиксируются данные о 43 погибших, тысячах раненых и роли национальной гвардии. Отмечено влияние беспорядков на дальнейший\\nупадок города и социальные программы восстановительного периода.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards_mechanics", "title": "Награды и механики", "body": "XP: 5000, Валюта: 0, Репутация «История»: +30. Игрок получает достижение «Свидетель Беспорядков».\\nОсновные механики — историческое расследование, социальные проверки и моральные выборы на почве расового напряжения.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 341, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-detroit-1967-riots"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Беспорядки 1967 года» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

