INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-amsterdam-2029-van-gogh-museum', 'Амстердам 2020-2029 — «Музей Ван Гога»', 'Игрок слушает аудио-гид, взаимодействует с экспонатами и принимает решения в эмоциональных сценах.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-amsterdam-2029-van-gogh-museum", "title": "Амстердам 2020-2029 — «Музей Ван Гога»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["amsterdam", "art", "museum"], "topics": ["fine-arts", "emotional-storytelling"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "education-service", "tourism-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-008-van-gogh-museum.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурной линии города не хватало глубокого музея с эмоциональным арком Ван Гога.", "goal": "Дать игроку curated-тур по ключевым картинам, письмам и биографии художника.", "essence": "Игрок слушает аудио-гид, взаимодействует с экспонатами и принимает решения в эмоциональных сценах.", "key_points": ["Показывает контраст между яркими картинами и тяжёлой судьбой художника.", "Использует модули mood-system для изменения восприятия сцен.", "Итоговая запись открывает тематические события LiveOps."]}, "content": {"sections": [{"id": "entry", "title": "Галерея шедевров", "body": "Игрок выбирает путь через «Подсолнухи», «Едоков картофеля» и автопортреты. Каждый экспонат сопровождается комментариями куратора.\\n", "mechanics_links": ["mechanics/culture/audio-guide.yaml"], "assets": []}, {"id": "letters", "title": "Письма Тео", "body": "В интерактивной комнате письма озвучены актёрами. Игрок читает ответы и фиксирует эмоциональные реакции.\\n", "mechanics_links": ["mechanics/narrative/emotion-tracker.yaml"], "assets": []}, {"id": "atelier", "title": "Виртуальная мастерская", "body": "Здесь можно поэкспериментировать с палитрой Ван Гога. Удержание кистей в нужных цветовых схемах даёт бонусы настроения.\\n", "mechanics_links": ["mechanics/creation/painting-mini-game.yaml"], "assets": []}, {"id": "reflection", "title": "Комната тишины", "body": "Завершение проходит в затемнённой комнате. Игрок выбирает, как интерпретировать наследие художника и получает эмоциональный бафф.\\n", "mechanics_links": ["mechanics/mood/reflection-choice.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, расходы 20 еддис.\\n- Атрибут «Культура» +20, ачивка «Ценитель Ван Гога».\\n- Открыта серия событий «Письма Тео» в культурном расписании.\\n", "mechanics_links": ["mechanics/progression/event-unlock.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 750, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Музей Ван Гога» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

