INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-amsterdam-2029-tulip-mania', 'Амстердам 2020-2029 — «Тюльпаномания»', 'Игрок путешествует между музеем и парком Keukenhof, участвует в симуляции торгов и анализирует последствия.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-amsterdam-2029-tulip-mania", "title": "Амстердам 2020-2029 — «Тюльпаномания»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["amsterdam", "tulips", "economy"], "topics": ["historical-economy", "speculative-markets"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "education-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-009-tulip-mania.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экономической ветке Амстердама требовался сюжет об историческом финансовом пузыре и его влиянии.", "goal": "Продемонстрировать механику спекуляции, кризиса и современного наследия тюльпанных полей.", "essence": "Игрок путешествует между музеем и парком Keukenhof, участвует в симуляции торгов и анализирует последствия.", "key_points": ["Показывает, как редкие луковицы превратились в актив с ценой дома.", "Симуляция торгов обучает управлению рисками.", "В финале игрок помогает современным фермерам планировать посадки."]}, "content": {"sections": [{"id": "archive", "title": "Архив тюльпаномании", "body": "В музее игрок изучает документы 1637 года, читает хронику сделок и смотрит на коллекцию редких луковиц.\\n", "mechanics_links": ["mechanics/education/historical-review.yaml"], "assets": []}, {"id": "exchange", "title": "Биржа спекулянтов", "body": "Симуляция торгов требует покупать и продавать фьючерсы на тюльпаны. Ошибки приводят к мгновенным убыткам.\\n", "mechanics_links": ["mechanics/economy/market-sim.yaml"], "assets": []}, {"id": "collapse", "title": "День краха", "body": "Игрок наблюдает массовые банкротства и должен выбрать: помочь ли соседям или сохранить капитал.\\n", "mechanics_links": ["mechanics/narrative/moral-choice.yaml"], "assets": []}, {"id": "keukenhof", "title": "Парк Keukenhof", "body": "Весенний визит показывает 7 миллионов тюльпанов. Игрок помогает садовникам планировать экспозиции и рассказывает туристам историю пузыря.\\n", "mechanics_links": ["mechanics/world/horticulture-task.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP.\\n- Атрибут «Экономика» +20, ачивка «Знаток пузырей».\\n- Доступ к событию «Весенние биржи» в LiveOps-календаре.\\n", "mechanics_links": ["mechanics/progression/event-unlock.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 751, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Тюльпаномания» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EX