INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-amsterdam-2020-2029-dutch-tolerance', 'Амстердам 2020-2029 — Голландская толерантность', 'Игрок встречается с чиновниками, предпринимателями и активистами, формируя собственную позицию относительно толерантности.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-amsterdam-2020-2029-dutch-tolerance", "title": "Амстердам 2020-2029 — Голландская толерантность", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["amsterdam", "tolerance", "gedogen"], "topics": ["social_philosophy", "regulatory_policy"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "diplomacy-service", "liveops-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-010-dutch-tolerance.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линии Амстердама не хватало квеста, объясняющего феномен «gedogen» и систему легализованной свободы.", "goal": "Показать игроку, как политика терпимости влияет на общество и экономика Нидерландов.", "essence": "Игрок встречается с чиновниками, предпринимателями и активистами, формируя собственную позицию относительно толерантности.", "key_points": ["Легализация рассматривается как инструмент контроля, а не вседозволенности.", "История решений по марихуане, проституции, бракам и эвтаназии раскрывается через встречи.", "Финальный выбор определяет отношение фракций к игроку и открывает социальные ветки."]}, "content": {"sections": [{"id": "philosophy", "title": "Ввод в «gedogen»", "body": "Игрок посещает муниципальный центр и знакомится с философией прагматической терпимости. Доклады и архивы объясняют, почему государство\\nпредпочитает регулировать вместо запрета.\\n", "mechanics_links": [], "assets": []}, {"id": "legalization_cases", "title": "Истории легализации", "body": "Серия сцен с экспертами: кофешопами, союзом работников секса, ЛГБТ-организациями и врачами. Каждая встреча раскрывает аргументы за и\\nпротив, влияя на настроение населения.\\n", "mechanics_links": [], "assets": []}, {"id": "civic_panel", "title": "Панель граждан", "body": "Игрок участвует в общественных слушаниях, где обсуждаются спорные вопросы. Выборы аргументов дают репутацию у разных фракций\\n(консерваторы, прагматики, активисты).\\n", "mechanics_links": [], "assets": []}, {"id": "decision", "title": "Личный манифест", "body": "В финале игрок формулирует манифест о границах свободы. Документ влияет на будущие события Амстердама и открывает спецмиссии с\\nliveops-командой.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP, философия +25.\\n- Титул «Философ толерантности» и доступ к дискуссиям об этике.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 752, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Голландская толерантность» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest