INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-louvre-heist', 'Париж 2020-2029 — Ограбление Лувра', 'Игрок планирует и осуществляет кражу Моны Лизы, выбирая между стелсом, подделкой и хакерством, после чего скрывается с национальным артефактом.', 'main', 30, 50, '{"completed_quests": [], "flags": []}'::jsonb, '[{"id": "plan_heist", "type": "interaction", "description": "Изучить охрану и выбрать метод проникновения", "required": true}, {"id": "prepare_equipment", "type": "interaction", "description": "Подготовить оборудование для обхода лазерной защиты", "required": true}, {"id": "infiltrate_louvre", "type": "location", "description": "Проникнуть в Лувр выбранным методом", "required": true}, {"id": "reach_mona_lisa", "type": "location", "description": "Добраться до зала Джоконды", "required": true}, {"id": "disable_security", "type": "interaction", "description": "Отключить датчики и системы безопасности", "required": true}, {"id": "extract_painting", "type": "interaction", "description": "Извлечь картину", "required": true}, {"id": "escape", "type": "location", "description": "Скрыться по заранее подготовленным маршрутам", "required": true}]'::jsonb, '{"experience": 10000, "money": {"type": "eddies", "value": 1000000000}, "reputation": {}, "unlocks": {"achievements": [{"id": "louvre_heist_master", "name": "Мастер ограбления Лувра"}], "flags": ["interpol_wanted"]}}'::jsonb, '[{"id": "stealth_approach", "condition": "method == ''stealth''", "description": "Стелс через крышу"}, {"id": "substitution_approach", "condition": "method == ''substitution''", "description": "Подмена картины"}, {"id": "hacking_approach", "condition": "method == ''hacking''", "description": "Взлом систем"}]'::jsonb, '{"metadata": {"id": "canon-quest-paris-louvre-heist", "title": "Париж 2020-2029 — Ограбление Лувра", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T12:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["paris", "heist", "art"], "topics": ["high-risk-quests", "cultural-heritage"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "content-world-quests-western-europe", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/paris/2020-2029/quest-002-louvre-heist.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест «Ограбление Лувра» не был представлен в структурированном формате и не участвовал в пайплайнах проверки.", "goal": "Перенести high-tier heist сценарий Парижа в YAML, сохранив этапы, угрозы и последствия.", "essence": "Игрок планирует и осуществляет кражу Моны Лизы, выбирая между стелсом, подделкой и хакерством, после чего скрывается с национальным артефактом.", "key_points": ["Сценарий включает многоступенчатое планирование, разные способы проникновения и работу с системами безопасности.", "Региональные детали подчёркивают Пирамиду Лувра, толпы туристов и пуленепробиваемое стекло картины.", "Награды огромные, но приводят к международному розыску и реакции Интерпола."]}, "quest_definition": {"quest_type": "main", "level_min": 30, "level_max": 50, "requirements": {"completed_quests": [], "flags": []}, "objectives": [{"id": "plan_heist", "type": "interaction", "description": "Изучить охрану и выбрать метод проникновения", "required": true}, {"id": "prepare_equipment", "type": "interaction", "description": "Подготовить оборудование для обхода лазерной защиты", "required": true}, {"id": "infiltrate_louvre", "type": "location", "description": "Проникнуть в Лувр выбранным методом", "required": true}, {"id": "reach_mona_lisa", "type": "location", "description": "Добраться до зала Джоконды", "required": true}, {"id": "disable_security", "type": "interaction", "description": "Отключить датчики и системы безопасности", "required": true}, {"id": "extract_painting", "type": "interaction", "description": "Извлечь картину", "required": true}, {"id": "escape", "type": "location", "description": "Скрыться по заранее подготовленным маршрутам", "required": true}], "rewards": {"experience": 10000, "money": {"type": "eddies", "value": 1000000000}, "reputation": {}, "unlocks": {"achievements": [{"id": "louvre_heist_master", "name": "Мастер ограбления Лувра"}], "flags": ["interpol_wanted"]}}, "branches": [{"id": "stealth_approach", "condition": "method == ''stealth''", "description": "Стелс через крышу"}, {"id": "substitution_approach", "condition": "method == ''substitution''", "description": "Подмена картины"}, {"id": "hacking_approach", "condition": "method == ''hacking''", "description": "Взлом систем"}]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест описывает легендарное ограбление Лувра с целью похищения «Моны Лизы». Требует продуманной подготовки, выбора команды\\nи эксплуатации исторических прецедентов.\\n", "mechanics_links": [], "assets": []}, {"id": "planning", "title": "Планирование и методы проникновения", "body": "Игрок изучает охрану, выбирает один из трёх методов — стелс через крышу, подмену картины или взлом систем — и подготавливает\\nоборудование для обхода лазерной защиты.\\n", "mechanics_links": [], "assets": []}, {"id": "execution", "title": "Исполнение и эвакуация", "body": "Проникновение в зал Джоконды, отключение датчиков, извлечение картины и бегство по заранее подготовленным маршрутам.\\nВарианты исхода — тихое исчезновение или экстремальный побег.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "10 000 XP, 1 000 000 000 ed на чёрном рынке, сам артефакт и мгновенный международный розыск. Механики — heist высокой\\nсложности, управление стрессом и этическая дилемма культурной кражи.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 622, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-paris-louvre-heist"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Ограбление Лувра» в формат знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches (включая три метода проникновения). Обновлен github_issue на 622."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

