INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2020-2029-berlin-wall-memorial', 'Берлин 2020-2029 — Мемориал Берлинской стены', 'Игрок исследует стену, её символы и встречи с очевидцами, формируя понимание свободы и разделения.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2020-2029-berlin-wall-memorial", "title": "Берлин 2020-2029 — Мемориал Берлинской стены", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "berlin-wall", "cold-war"], "topics": ["historical_memory", "urban_exploration"], "related_systems": ["narrative-service", "education-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2020-2029/quest-001-berlin-wall-memorial.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Берлинской линии требовался квест, погружающий в историю разделенного города и опыта холодной войны.", "goal": "Провести игрока через сохранившиеся участки стены, граффити East Side Gallery и мемориалы погибшим.", "essence": "Игрок исследует стену, её символы и встречи с очевидцами, формируя понимание свободы и разделения.", "key_points": ["Экспозиция связывает прошлое с современным Берлином.", "VR-реконструкция показывает жизнь по обе стороны стены.", "Завершение подчеркивает важность памяти и свободы."]}, "content": {"sections": [{"id": "gallery", "title": "East Side Gallery", "body": "Игрок проходит вдоль 1,3 км сохранившейся стены, изучает граффити «Братский поцелуй» и «Trabant через стену», активирует аудиогиды\\nхудожников.\\n", "mechanics_links": [], "assets": []}, {"id": "checkpoint", "title": "Checkpoint Charlie", "body": "В интерактивной сцене игрок наблюдает реконструкцию пограничного перехода и узнает истории побегов. Возможен выбор, какую версию\\nистории представить туристам.\\n", "mechanics_links": [], "assets": []}, {"id": "memorial", "title": "Мемориал жертв стены", "body": "Посещение мемориала с именами погибших. Игрок запускает AR-портреты и оставляет символ памяти, получая эмпатический эффект.\\n", "mechanics_links": [], "assets": []}, {"id": "vr", "title": "VR «Разделенный Берлин»", "body": "Симуляция 1960-х годов показывает повседневную жизнь по обе стороны стены. Игрок делает выбор, как отразить историю в образовательных\\nпрограммах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP, культура +10.\\n- Ачивка «Свидетель истории» и доступ к экскурсиям холодной войны.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 741, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест о мемориале Берлинской стены адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

