', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-blitz-memory", "title": "Лондон 2061-2077 — Память Блица", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "history", "memory"], "topics": ["main_story", "narrative"], "related_systems": ["narrative-service", "memory-archive", "reputation-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2061-2077/quest-040-blitz-memory.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История о последнем ветеране Блица находилась в Markdown и не была структурирована.", "goal": "Сохранить эмоциональный квест о памяти и стойкости Лондона в формате знаний.", "essence": "Игрок встречает столетнего ветерана, записывает его воспоминания о Блице и исполняет последнее желание, становясь хранителем памяти.\\n", "key_points": ["Описаны этапы поиска ветерана, оцифровки воспоминаний и прощания.", {"Зафиксированы региональные детали": "укрытия в метро, сирены тревог, дух «Keep Calm»."}, {"Определены награды": "опыт, титул и эмоциональный резонанс."}]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Встреча с ветераном Блица показывает цену британской стойкости. Игрок помогает сохранить его память.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Ход квеста", "body": "1. Найти ветерана в доме престарелых и услышать его истории.\\n2. Оцифровать воспоминания для архива города.\\n3. Исполнить последнее желание героя и присутствовать при прощании.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "2 500 XP, титул «Хранитель Памяти» и доступ к историческим материалам Лондона. Квест дарит сильный эмоциональный эффект.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-blitz-memory"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Память Блица» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

