INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-seine-romance', 'Париж 2020-2029 — Романтика Сены', 'Игрок арендует bateau, проходит под культовыми мостами, наблюдает Париж на закате и получает бонус к романтическим показателям.', 'exploration', 5, 50, '{"completed_quests": [], "flags": []}'::jsonb, '[{"id": "rent_boat", "type": "interaction", "description": "Арендовать bateau-mouche для двоих", "required": true}, {"id": "start_cruise", "type": "location", "description": "Стартовать маршрут из центра города", "required": true}, {"id": "pass_bridges", "type": "location", "description": "Проплыть под Pont Neuf, Pont Alexandre III и Pont des Arts", "required": true}, {"id": "see_eiffel", "type": "location", "description": "Встретить вид на Эйфелеву башню с воды", "required": true}, {"id": "kiss_under_bridge", "type": "interaction", "description": "Совершить поцелуй под мостом", "required": true}]'::jsonb, '{"experience": 1000, "money": {"type": "eddies", "value": -200}, "reputation": {"romance": 25}, "unlocks": {"achievements": [{"id": "paris_romantic", "name": "Романтик Парижа"}], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-seine-romance", "title": "Париж 2020-2029 — Романтика Сены", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["paris", "romance", "river"], "topics": ["social-storytelling", "lifestyle"], "related_systems": ["narrative-service", "live-ops"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "content-world-quests-western-europe", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/paris/2020-2029/quest-003-seine-romance.md", "visibility": "internal", "audience": ["narrative", "content", "vision"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Романтический квест по Сене находился в Markdown и не был подключен к автоматизированным пайплайнам.", "goal": "Формализовать круиз по Сене с ключевыми мостами, атмосферой и наградами в YAML.", "essence": "Игрок арендует bateau, проходит под культовыми мостами, наблюдает Париж на закате и получает бонус к романтическим показателям.", "key_points": ["Подчёркнуты мосты Pont Neuf, Pont Alexandre III и Pont des Arts с традицией любви.", "Хронология шагов фиксирует аренду судна, маршрут и кульминацию с поцелуем под мостом.", "Награды усиливают параметр «Романс» и дают ачивку «Романтик Парижа»."]}, "quest_definition": {"quest_type": "exploration", "level_min": 5, "level_max": 50, "requirements": {"completed_quests": [], "flags": []}, "objectives": [{"id": "rent_boat", "type": "interaction", "description": "Арендовать bateau-mouche для двоих", "required": true}, {"id": "start_cruise", "type": "location", "description": "Стартовать маршрут из центра города", "required": true}, {"id": "pass_bridges", "type": "location", "description": "Проплыть под Pont Neuf, Pont Alexandre III и Pont des Arts", "required": true}, {"id": "see_eiffel", "type": "location", "description": "Встретить вид на Эйфелеву башню с воды", "required": true}, {"id": "kiss_under_bridge", "type": "interaction", "description": "Совершить поцелуй под мостом", "required": true}], "rewards": {"experience": 1000, "money": {"type": "eddies", "value": -200}, "reputation": {"romance": 25}, "unlocks": {"achievements": [{"id": "paris_romantic", "name": "Романтик Парижа"}], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест описывает приватный круиз по Сене во время заката, раскрывая атмосферу «Paris, je t''aime» и ритуалы мостов,\\nгде туристы и жители закрепляют любовные истории.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы круиза", "body": "1. Арендовать bateau-mouche для двоих.\\n2. Стартовать маршрут из центра города и налить бокалы шампанского.\\n3. Проплыть под Pont Neuf, Pont Alexandre III и Pont des Arts, активируя локальные события.\\n4. Встретить вид на Эйфелеву башню с воды.\\n5. Совершить поцелуй под мостом и закрепить обещание любви.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указаны символика Pont des Arts (замки любви), собор Нотр-Дам на Иль-де-ла-Сите, закаты над Сеной и традиции шампанского\\nна борту, создающие кинематографичный опыт.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "Игрок получает 1 000 XP, тратит 200 едди на аренду, повышает параметр «Романс» на 25 и открывает достижение «Романтик Парижа».\\nМеханики включают социальное взаимодействие, атмосферный маршрут и романтические триггеры.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 613, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-paris-seine-romance"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Романтика Сены» в формат знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 613."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type =