INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2020-2029-berghain-techno', 'Берлин 2020-2029 — Бергхайн техно', 'Игрок балансирует между стилем, отношением и выносливостью, чтобы пережить 24+ часа техно-марафона.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2020-2029-berghain-techno", "title": "Берлин 2020-2029 — Бергхайн техно", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "nightlife"], "topics": ["subculture", "social_challenge"], "related_systems": ["narrative-service", "social-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2020-2029/quest-002-berghain-techno.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Берлинской ночной сцене не хватало миссии, отражающей культовый статус Berghain и сложности попадания внутрь.", "goal": "Смоделировать путь игрока через очередь, фейс-контроль и многодневный рейв.", "essence": "Игрок балансирует между стилем, отношением и выносливостью, чтобы пережить 24+ часа техно-марафона.", "key_points": ["Очередь и фейс-контроль задают тон социального испытания.", "Внутри клуба запрещены камеры, упор на атмосферу и музыку.", "Продолжительные вечеринки проверяют ресурсы и отношения."]}, "content": {"sections": [{"id": "queue", "title": "Очередь у входа", "body": "Игрок подбирает стиль, изучает поведение завсегдатаев и готовится к решающему взгляду охраны. Система оценивает харизму и\\nсоответствие кодексу клуба.\\n", "mechanics_links": [], "assets": []}, {"id": "face_control", "title": "Фейс-контроль Sven Marquardt", "body": "Проверка сочетает RNG и выбор реплик. Успех открывает клуб, отказ — альтернативные пути (afterparty, новая попытка).\\n", "mechanics_links": [], "assets": []}, {"id": "dancefloor", "title": "Техно-марафон", "body": "Ритмическая мини-игра и менеджмент энергии на протяжении 24 часов. Игрок встречает диджеев, участвует в liveops-ивентах и\\nподдерживает социальные связи.\\n", "mechanics_links": [], "assets": []}, {"id": "recovery", "title": "После вечеринки", "body": "Финальная сцена с рассветом у выхода. Игрок решает, как использовать новый статус — получить доступ к андеграундной сети или\\nподелиться опытом в медиа.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Успех: 3 000 XP, репутация техно-сцены +20, титул «Берлинский рейвер».\\n- Провал: эмоциональный опыт отказа и мини-квест на переосмысление образа.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 742, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Бергхайн техно» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

