INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2020-2029-kreuzberg-squat', 'Берлин 2020-2029 — Сквот Кройцберга', 'Игрок помогает занять пустующий дом, оформляет пространство и защищает его от рейда.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "scout_buildings", "text": "Вместе с локальными активистами изучить пустующие дома, собрать данные о владельцах и полиции. Подготовка снижает риск рейда", "type": "investigate", "target": "kreuzberg_empty_buildings", "count": 3, "optional": false}, {"id": "occupy_building", "text": "Помочь коммуне вскрыть дом, распределить комнаты, организовать электропитание и общую кухню. Выбрать специализацию внутри сквота", "type": "perform", "target": "kreuzberg_squat_setup", "count": 1, "optional": false}, {"id": "participate_assembly", "text": "Участвовать в общем собрании, формировать правила: распределение ресурсов, расписание дежурств, взаимодействие с соседями. Предложить инициативы", "type": "interact", "target": "squat_assembly_meeting", "count": 1, "optional": false}, {"id": "defend_from_raid", "text": "Защитить сквот от полицейского рейда. Выбрать стратегию — переговоры, баррикады или медиаподдержку", "type": "perform", "target": "squat_defense_raid", "count": 1, "optional": false}]'::jsonb, '{"experience": 2500, "currency": 0, "items": [{"item_id": "illegal_squat_housing", "name": "Нелегальное жильё в сквоте", "quantity": 1}], "reputation": {"anarchist_collectives": 15, "kreuzberg_community": 10}, "flags": ["kreuzberg_squat_quest_completed", "underground_missions_unlocked", "squat_network_access"]}'::jsonb, '[{"id": "negotiation_path", "unlock_condition": {"type": "choice_made", "choice_id": "defense_strategy", "value": "negotiation"}, "consequences": {"currency": 0, "reputation": {"anarchist_collectives": 5, "berlin_authorities": -5}, "flags": ["diplomatic_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "diplomatic", "location": "berlin"}]}}, {"id": "barricade_path", "unlock_condition": {"type": "choice_made", "choice_id": "defense_strategy", "value": "barricade"}, "consequences": {"currency": 0, "reputation": {"anarchist_collectives": 10, "berlin_authorities": -10}, "flags": ["resistance_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "resistance", "location": "berlin"}]}}, {"id": "media_support_path", "unlock_condition": {"type": "choice_made", "choice_id": "defense_strategy", "value": "media"}, "consequences": {"currency": 0, "reputation": {"anarchist_collectives": 8, "berlin_media": 5}, "flags": ["media_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "media", "location": "berlin"}]}}]'::jsonb, '{"metadata": {"id": "quest-berlin-2020-2029-kreuzberg-squat", "title": "Берлин 2020-2029 — Сквот Кройцберга", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "kreuzberg", "squatters"], "topics": ["urban_reclamation", "anarchist_collectives"], "related_systems": ["narrative-service", "faction-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2020-2029/quest-005-kreuzberg-squat.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линии Берлина не хватало квеста о культуре сквотов и борьбе за городское пространство.", "goal": "Погрузить игрока в анархистск