INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-catacombs-exploration', 'Париж 2020-2029 — Исследование катакомб', 'Группа игроков проникает через нелегальный вход, ориентируется в лабиринте костей, сталкивается с катафилами и пытается отыскать легендарную комнату Z, рискуя навсегда потеряться в подземном Париже.', 'exploration', 20, 50, '{"completed_quests": [], "flags": []}'::jsonb, '[{"id": "find_entrance", "type": "location", "description": "Найти нелегальный вход в катакомбы", "required": true}, {"id": "descend", "type": "location", "description": "Спуститься на глубину двадцати метров", "required": true}, {"id": "navigate_tunnels", "type": "location", "description": "Пройти по сети туннелей, используя метки и датчики", "required": true}, {"id": "meet_cataphiles", "type": "interaction", "description": "Встретить катафилов, решить вопрос сотрудничества или конфликта", "required": true}, {"id": "find_room_z", "type": "location", "description": "Отыскать скрытую комнату Z", "required": true}]'::jsonb, '{"experience": 3000, "money": {"type": "eddies", "value": 0}, "reputation": {}, "unlocks": {"achievements": [{"id": "catacombs_explorer", "name": "Исследователь катакомб"}], "flags": ["catacombs_visited"]}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-catacombs-exploration", "title": "Париж 2020-2029 — Исследование катакомб", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T12:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["paris", "catacombs", "exploration"], "topics": ["urban_adventure", "horror"], "related_systems": ["narrative-service", "exploration-system"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "content-world-quests-western-europe", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/paris/2020-2029/quest-007-catacombs-exploration.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест об исследовании катакомб был в Markdown и не участвовал в валидациях знаний.", "goal": "Перенести катакомбный сценарий в YAML с ясной структурой этапов, угроз и наград.", "essence": "Группа игроков проникает через нелегальный вход, ориентируется в лабиринте костей, сталкивается с катафилами и пытается отыскать легендарную комнату Z, рискуя навсегда потеряться в подземном Париже.", "key_points": [{"Фиксированы ключевые этапы": "поиск входа, спуск, навигация, контакт с катафилами, финальный поиск комнаты Z."}, "Подчёркнуты региональные контексты катакомб, латинские надписи и подпольные вечеринки.", "Описаны награды с артефактами и рисками, а также механики хоррора и навигации."]}, "quest_definition": {"quest_type": "exploration", "level_min": 20, "level_max": 50, "requirements": {"completed_quests": [], "flags": []}, "objectives": [{"id": "find_entrance", "type": "location", "description": "Найти нелегальный вход в катакомбы", "required": true}, {"id": "descend", "type": "location", "description": "Спуститься на глубину двадцати метров", "required": true}, {"id": "navigate_tunnels", "type": "location", "description": "Пройти по сети туннелей, используя метки и датчики", "required": true}, {"id": "meet_cataphiles", "type": "interaction", "description": "Встретить катафилов, решить вопрос сотрудничества или конфликта", "required": true}, {"id": "find_room_z", "type": "location", "description": "Отыскать скрытую комнату Z", "required": true}], "rewards": {"experience": 3000, "money": {"type": "eddies", "value": 0}, "reputation": {}, "unlocks": {"achievements": [{"id": "catacombs_explorer", "name": "Исследователь катакомб"}], "flags": ["catacombs_visited"]}}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Игроки отправляются в катакомбы под Парижем, где хранятся останки миллионов людей. Сценарий строится на атмосферном\\nхорроре и необходимости координации в узких туннелях.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Найти нелегальный вход в катакомбы, обходя охрану и датчики.\\n2. Спуститься на глубину двадцати метров и подготовиться к условиям подземелья.\\n3. Пройти по сети туннелей, используя метки, датчики и дроны для ориентации.\\n4. Встретить катафилов, решить вопрос сотрудничества или конфликта.\\n5. Отыскать скрытую комнату Z и вынести артефакты, избежав потерянных маршрутов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указаны стены из черепов, узкие коридоры, латинские надписи и подпольные вечеринки. Центральная отсылка — фраза\\n«Arrête! C''est ici l''empire de la Mort» при входе в комплекс.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Участники получают 3 000 XP, находят уникальные артефакты и повышают известность в подпольных кругах. Основной риск —\\nперманентное исчезновение персонажа, если группа теряет навигацию.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 617, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-paris-catacombs-exploration"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Исследование катакомб» в структуру знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 617."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

