INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2029-currywurst-vendor', 'Берлин 2020-2029 — Продавец карривурст', 'Игрок исследует локации уличной еды, настраивает рецепт карри-кетчупа и становится постоянным гостем продавца.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_currywurst_stand", "text": "Получить подсказки от местных NPC и сканировать карту уличной еды. Выбрать будку с лучшими отзывами и подходящим уровнем остроты", "type": "travel", "target": "berlin_street_food_map", "count": 1, "optional": false}, {"id": "customize_currywurst", "text": "Пройти мини-игру по настройке рецепта: с кожицей или без, уровень карри и гарнир. Баланс вкуса влияет на баффы и реакцию очереди", "type": "perform", "target": "currywurst_customization_minigame", "count": 1, "optional": false}, {"id": "learn_vendor_story", "text": "Выслушать историю владельца о легенде Херте Хойвер. Задать вопросы, открывая коллекционные карточки с фактами о карривурст", "type": "interact", "target": "currywurst_vendor_npc", "count": 1, "optional": false}, {"id": "become_regular_customer", "text": "Стать постоянным клиентом: повторные визиты и участие в челленджах «самый острый соус» повышают статус. Открываются скидки и доступ к ночным ивентам", "type": "perform", "target": "currywurst_loyalty_program", "count": 3, "optional": false}]'::jsonb, '{"experience": 500, "currency": -5, "items": [{"item_id": "curry_boost_buff", "name": "Бафф «Curry Boost» (+15% HP на 6 часов)", "quantity": 1}, {"item_id": "currywurst_expert_title", "name": "Титул «Currywurst эксперт»", "quantity": 1}], "reputation": {"berlin_street_food": 10, "berlin_culture": 5}, "flags": ["currywurst_vendor_quest_completed", "street_food_events_unlocked", "currywurst_loyalty_unlocked"]}'::jsonb, '[{"id": "spicy_challenge_path", "unlock_condition": {"type": "objective_completion", "objective_id": "become_regular_customer", "min_count": 3}, "consequences": {"currency": 0, "reputation": {"berlin_street_food": 10}, "flags": ["spicy_challenge_events_unlocked"], "unlocked_content": [{"event_type": "liveops", "category": "gastronomy", "location": "berlin"}]}}, {"id": "vendor_friendship_path", "unlock_condition": {"type": "objective_completion", "objective_id": "learn_vendor_story", "min_count": 1}, "consequences": {"currency": 0, "reputation": {"berlin_culture": 10}, "flags": ["vendor_quests_unlocked"], "unlocked_content": [{"quest_type": "side", "category": "social", "location": "berlin"}]}}]'::jsonb, '{"metadata": {"id": "quest-berlin-2029-currywurst-vendor", "title": "Берлин 2020-2029 — Продавец карривурст", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "street-food", "culinary"], "topics": ["culinary_streetfood", "urban_daily_life"], "related_systems": ["narrative-service", "social-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2020-2029/quest-006-currywurst-vendor.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная линия Берлина нуждалась в бытовом квесте о культовой уличной еде.", "goal": "Позволить игроку открыть легендарную будку карривурст и выстроить отношения с владельцем.", "essence": "Игрок исследует локации уличной еды, настраивает рецепт карри-кетчупа и становится постоянным гостем продавца.", "key_points": ["Под