INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-rome-2029-001-colosseum-gladiator', 'Рим: турнир Колизея', 'Игрок вступает в бойцовский турнир, сражается под взглядами многотысячной толпы и управляет вниманием спонсоров и граждан.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-rome-2029-001-colosseum-gladiator", "title": "Рим: турнир Колизея", "document_type": "canon", "category": "city-quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T07:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["rome", "colosseum", "combat"], "topics": ["city-lore"], "related_systems": ["combat-service", "social-mechanics", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/rome/2020-2029/quest-001-colosseum-gladiator.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик описывал гладиаторский турнир списком фактов и не связывал его с сервисами боёв и реакцией толпы.", "goal": "Формализовать сценарий современного турнира в Колизее с выбором вооружения, стадиями боёв и влиянием аудитории.", "essence": "Игрок вступает в бойцовский турнир, сражается под взглядами многотысячной толпы и управляет вниманием спонсоров и граждан.", "key_points": ["Колизей возвращён как арена высокорисковых PvE/PvP боёв.", "Выбор вооружения и стиля влияет на доступные тактики и награды.", "Решения толпы и спонсоров меняют репутацию и доступ к будущим событиям."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ROME-2029-001`\\n- Формат: аренный турнир, сложность высокая, solo, длительность 2–4 часа.\\n- Локация: Колизей (Colosseo), модернизированный под киберспорт-бои.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "enrollment", "title": "Регистрация и подготовка", "body": "1. Оплатить вступительный взнос и пройти допуск медиков.\\n2. Выбрать класс вооружения: гладиус+скутум, трезубец+сеть или двойные клинки.\\n3. Нанять тренера и получить временные баффы через progression-service.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "tournament_flow", "title": "Ход турнира", "body": "1. Серия из пяти раундов с уникальными противниками (AI+игроки).\\n2. Использование подземных ходов и ловушек древних hypogeum.\\n3. Финальная дуэль с чемпионом арены, где решающим фактором становится поддержка толпы.\\n", "mechanics_links": ["mechanics/combat/combat-archetypes.yaml"]}, {"id": "crowd_influence", "title": "Влияние аудитории", "body": "- Поличе verso: толпа и патроны определяют, добить ли соперника или пощадить.\\n- Репутация в социальных сервисах открывает новые спонсорские контракты.\\n- Нарушение кодекса арены ведёт к штрафам и блокировкам участия.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 10000 XP, 5000 кредитов на покрытие расходов, шанс на легендарное оружие «Gladius Maximus».\\n- Репутация «Champion of Colosseum», доступ к элитным аренам Европы.\\n- Бафф «Arena Adrenaline» (+20% скорость реакции на 12 часов).\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия турнира Колизея в структурированный YAML со связями сервисов."}], "validation": {"checksum": 