INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2029-tv-tower-hack', 'Берлин 2020-2029 — Взлом телебашни', 'Игрок прокрадывается на башню ГДР, взламывает оборудование и решает, кому послужит эфир.', 'side', 10, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "infiltrate_tower", "text": "Ночной стелс по Alexanderplatz: отвлечь охрану, взломать пропуска и отключить камеры", "type": "stealth", "target": "tv_tower_security", "count": 1, "optional": false}, {"id": "ascend_to_sphere", "text": "Подняться на лифте на 203 метра в сферу, решить отключить датчики или замаскировать сигнал", "type": "travel", "target": "tv_tower_elevator", "count": 1, "optional": false}, {"id": "hack_equipment", "text": "Взломать оборудование: настроить частоты, перехватить цифровые ключи и обойти резервный сервер NetWatch", "type": "hack", "target": "tv_tower_broadcast_equipment", "count": 1, "optional": false}, {"id": "choose_message", "text": "Выбрать послание для трансляции: революционное обращение, арт-инсталляция или коммерческий спот", "type": "choice", "target": "broadcast_message_selection", "count": 1, "optional": false}, {"id": "escape_tower", "text": "Скрыться через сервисные тоннели после трансляции", "type": "stealth", "target": "tv_tower_service_tunnels", "count": 1, "optional": false}]'::jsonb, '{"experience": 8000, "currency": 100000, "items": [], "reputation": {"berlin_hacktivists": 25, "media_outlets": 15}, "flags": ["tv_tower_hack_completed", "voice_of_berlin_title_unlocked"]}'::jsonb, '[{"id": "revolutionary_message_path", "unlock_condition": {"type": "choice_made", "choice_id": "broadcast_message_selection", "value": "revolutionary"}, "consequences": {"reputation": {"berlin_hacktivists": 15, "authorities": -20}, "flags": ["revolutionary_message_broadcast", "wanted_level_increased"], "unlocked_content": [{"quest_type": "side", "category": "political", "location": "berlin"}]}}, {"id": "art_installation_path", "unlock_condition": {"type": "choice_made", "choice_id": "broadcast_message_selection", "value": "art"}, "consequences": {"reputation": {"berlin_artists": 20, "media_outlets": 10}, "flags": ["art_installation_broadcast"], "unlocked_content": [{"quest_type": "side", "category": "artistic", "location": "berlin"}]}}, {"id": "commercial_spot_path", "unlock_condition": {"type": "choice_made", "choice_id": "broadcast_message_selection", "value": "commercial"}, "consequences": {"currency": 50000, "reputation": {"corporations": 15, "authorities": -10}, "flags": ["commercial_spot_broadcast"], "unlocked_content": [{"quest_type": "side", "category": "corporate", "location": "berlin"}]}}]'::jsonb, '{"metadata": {"id": "quest-berlin-2029-tv-tower-hack", "title": "Берлин 2020-2029 — Взлом телебашни", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "hacktivism", "media"], "topics": ["cyber_operations", "political_influence"], "related_systems": ["narrative-service", "netrunner-service", "diplomacy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2020-2029/quest-010-tv-tower-hack.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Серии Берлина не хватало высокорискового хакерского квеста с глобальными последствиями.", "goal": "Провести игрока через проникновение на Fernsehturm и выбор трансляционного послания.", "essence": "Игрок прокрадывается на башню ГДР, взламывает оборудование и решает, кому послужит эфир.", "key_points": ["Отражает политическое наследие ГДР и культовый силуэт башни.", {"Многоэтапная операция": "стелс, лифт, взлом, выбор сообщения."}, "Исход влияет на репутацию фракций и поиск игрока службами."]}, "quest_definition": {"quest_type": "side", "level_min": 10, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "infiltrate_tower", "text": "Ночной стелс по Alexanderplatz: отвлечь охрану, взломать пропуска и отключить камеры", "type": "stealth", "target": "tv_tower_security", "count": 1, "optional": false}, {"id": "ascend_to_sphere", "text": "Подняться на лифте на 203 метра в сферу, решить отключить датчики или замаскировать сигнал", "type": "travel", "target": "tv_tower_elevator", "count": 1, "optional": false}, {"id": "hack_equipment", "text": "Взломать оборудование: настроить частоты, перехватить цифровые ключи и обойти резервный сервер NetWatch", "type": "hack", "target": "tv_tower_broadcast_equipment", "count": 1, "optional": false}, {"id": "choose_message", "text": "Выбрать послание для трансляции: революционное обращение, арт-инсталляция или коммерческий спот", "type": "choice", "target": "broadcast_message_selection", "count": 1, "optional": false}, {"id": "escape_tower", "text": "Скрыться через сервисные тоннели после трансляции", "type": "stealth", "target": "tv_tower_service_tunnels", "count": 1, "optional": false}], "rewards": {"experience": 8000, "currency": 100000, "items": [], "reputation": {"berlin_hacktivists": 25, "media_outlets": 15}, "flags": ["tv_tower_hack_completed", "voice_of_berlin_title_unlocked"]}, "branches": [{"id": "revolutionary_message_path", "unlock_condition": {"type": "choice_made", "choice_id": "broadcast_message_selection", "value": "revolutionary"}, "consequences": {"reputation": {"berlin_hacktivists": 15, "authorities": -20}, "flags": ["revolutionary_message_broadcast", "wanted_level_increased"], "unlocked_content": [{"quest_type": "side", "category": "political", "location": "berlin"}]}}, {"id": "art_installation_path", "unlock_condition": {"type": "choice_made", "choice_id": "broadcast_message_selection", "value": "art"}, "consequences": {"reputation": {"berlin_artists": 20, "media_outlets": 10}, "flags": ["art_installation_broadcast"], "unlocked_content": [{"quest_type": "side", "category": "artistic", "location": "berlin"}]}}, {"id": "commercial_spot_path", "unlock_condition": {"type": "choice_made", "choice_id": "broadcast_message_selection", "value": "commercial"}, "consequences": {"currency": 50000, "reputation": {"corporations": 15, "authorities": -10}, "flags": ["commercial_spot_broadcast"], "unlocked_content": [{"quest_type": "side", "category": "corporate", "location": "berlin"}]}}]}, "content": {"sections": [{"id": "infiltration", "title": "Проникновение на башню", "body": "Ночной стелс по Alexanderplatz: отвлечение охраны, взлом пропусков и отключение камер. Неудача вызывает усиленные патрули.\\n", "mechanics_links": ["mechanics/stealth/security-breach.yaml"], "assets": []}, {"id": "ascent", "title": "Подъём в сферу", "body": "Лифт поднимается на 203 метра. Игрок решает, отключать ли датчики или маскировать сигнал. От выбора зависит сложность финального взлома.\\n", "mechanics_links": ["mechanics/narrative/branching-choice.yaml"], "assets": []}, {"id": "hack", "title": "Взлом оборудования", "body": "Мини-игра уровня DC 22: настройка частот, перехват цифровых ключей и обход резервного сервера NetWatch.\\n", "mechanics_links": ["mechanics/netrunner/frequency-hack.yaml"], "assets": []}, {"id": "message", "title": "Выбор послания", "body": "Три сценария: революционное обращение, арт-инсталляция или коммерческий спот. Каждый изменяет репутацию и вызывает уникальные события.\\n", "mechanics_links": ["mechanics/diplomacy/faction-impact.yaml"], "assets": []}, {"id": "escape", "title": "Побег и последствия", "body": "После трансляции нужно скрыться через сервисные тоннели. Итоги записываются в мировые новости и запускают реакцию властей.\\n", "mechanics_links": ["mechanics/stealth/exfiltration.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 8 000 XP, потенциальный доход до 100 000 еддис.\\n- Титул «Голос Берлина», уникальные модификаторы розыска и медиа-кампаний.\\n", "mechanics_links": ["mechanics/progression/title-unlock.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 718, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлен Issue в начало файла. Добавлена полная структура quest_definition с objectives, rewards и branches согласно архитектуре системы квестов. Структура теперь полностью соответствует архитектуре."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Взлом телебашни» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

