INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-rome-2029-003-trevi-fountain', 'Рим: желание у фонтана Треви', 'Игрок участвует в традиции с монетами, взаимодействует с NPC и решает, использовать ли удачу ради личной выгоды или благотворительности.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-rome-2029-003-trevi-fountain", "title": "Рим: желание у фонтана Треви", "document_type": "canon", "category": "city-quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T07:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["rome", "tourism", "traditions"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/rome/2020-2029/quest-003-trevi-fountain.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание ритуала у фонтана Треви не содержало игровых стадий и экономического эффекта.", "goal": "Добавить структуру, выборы и эффекты удачи для туристического квеста в центре Рима.", "essence": "Игрок участвует в традиции с монетами, взаимодействует с NPC и решает, использовать ли удачу ради личной выгоды или благотворительности.", "key_points": ["Три типа пожеланий приводят к разным социальным бонусам.", "Экономика города реагирует на пожертвования и туристический поток.", "Возможны скрытые события ночью, отсылающие к кино «La Dolce Vita»."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ROME-2029-003`\\n- Формат: туристический ритуал, сложность низкая, solo, длительность до 1 часа.\\n- Локация: Fontana di Trevi, прилегающие улицы и сувенирные лавки.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "ritual", "title": "Ритуал монет", "body": "1. Прорваться через толпу и занять точку у фонтана.\\n2. Выполнить бросок монеты через плечо в зависимости от выбранного желания.\\n3. Зафиксировать мечту в социальном интерфейсе и получать бафф удачи.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "choices", "title": "Решения игрока", "body": "- Оставить пожертвование благотворительной организации города (увеличение репутации).\\n- Использовать удачу для доступа к эксклюзивным музеям.\\n- Нарушить правила ночью и получить скрытую сцену с купанием в фонтане (риск штрафа).\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, расходы 1–5 кредитов на монеты и сувениры.\\n- Бафф «Roman Luck» (+5% шанс редких событий на 24 часа).\\n- Ачивка «Вернусь в Рим» и доступ к романтической ветке города.\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Оформлен туристический квест у фонтана Треви в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

