INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vienna-sachertorte', 'Вена — Торт Захер', 'Игрок посещает Café Sacher, изучает историю рецепта и пробует легендарный десерт, получая временный бафф энергии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vienna-sachertorte", "title": "Вена — Торт Захер", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T20:32:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T20:32:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["vienna", "gastronomy", "luxury"], "topics": ["culinary_experience", "hospitality", "cultural_traditions"], "related_systems": ["crafting-system", "mood-system", "reputation-system"], "related_documents": [{"id": "canon-quest-vienna-coffee-culture", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/vienna/2020-2029/quest-003-sachertorte.md", "visibility": "internal", "audience": ["narrative", "economy", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Оригинальный текст не описывал проверку ингредиентов, экономику десерта и влияние на настроение игрока.", "goal": "Превратить посещение Hotel Sacher в интерактивную дегустацию с эффектами и культурными бонусами.", "essence": "Игрок посещает Café Sacher, изучает историю рецепта и пробует легендарный десерт, получая временный бафф энергии.", "key_points": ["Включены гастрономические этапы и спор с кондитерской Demel.", "Квест повышает престиж в сети роскошных заведений Вены.", "Игрок может разблокировать рецепт и редкие ингредиенты для кулинарной системы."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 30–60 минут.\\nНаграды: 800 XP, -12 едди, бафф «Энергия +15% на 4 часа».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Забронировать столик в Café Sacher и изучить историческую витрину десерта.\\n2. Заказать оригинальный торт Захер и изучить его слои через AR-скан.\\n3. Посетить экспозицию о создании рецепта и споре Sacher vs Demel.\\n4. Сфотографировать десерт для социальной ленты и получить отзывы подписчиков.\\n5. Приобрести набор ингредиентов для кулинарной мастерской и разблокировать рецепт.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "crafting-system открывает рецепт десерта при покупке набора.\\nmood-system применяет положительный эффект «Шоколадная эйфория».\\nreputation-system повышает статус в роскошных заведениях и культурных кругах.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Раскрыта история 1832 года, роль Франца Захера и секретного рецепта.\\nОписана роскошь Hotel Sacher, сервировка и соперничество с кондитерской Demel.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает ачивку «Захер гурман» и доступ к эксклюзивной дегустации десертов.\\nИзучение рецепта даёт бонус к продажам в собственной кофейне.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в YAML 1.1.0, добавлены гастрономические механики и эффекты настроения."}, {"version": "0.1.0", "date": "2025-11-03", "author": "narrative_team", "changes": "Markdown-описание дегустации торта Захер."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

