INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2040-2060-techno-utopia', 'Берлин 2040-2060 — Техно-утопия', 'Игрок захватывает заброшенный квартал, настраивает звуковые системы, вводит демократию танца и защищает утопию.', 'side', 12, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "takeover_district", "text": "Захватить заброшенный квартал с союзниками и установить периметр", "type": "interact", "target": "district_takeover", "count": 1, "optional": false}, {"id": "negotiate_neutrality", "text": "Договориться с соседними сообществами о нейтралитете", "type": "interact", "target": "neighborhood_negotiations", "count": 3, "optional": false}, {"id": "setup_sound_network", "text": "Настроить звуковую сеть района: колонки, генераторы и энергобаланс", "type": "interact", "target": "sound_network_setup", "count": 1, "optional": false}, {"id": "implement_dance_governance", "text": "Запустить систему голосования танцем на рейвах", "type": "interact", "target": "dance_governance_system", "count": 1, "optional": false}, {"id": "defend_autonomy", "text": "Защитить автономию от давления властей: информационная кампания, переговоры или баррикады", "type": "interact", "target": "autonomy_defense", "count": 1, "optional": false}, {"id": "choose_utopia_outcome", "text": "Принять финальное решение: сохранить независимость, пойти на компромисс или потерять район", "type": "interact", "target": "utopia_final_choice", "count": 1, "optional": false}]'::jsonb, '{"experience": 10000, "money": 0, "items": [{"item_id": "title_techno_utopia_founder", "quantity": 1}], "reputation": {}, "unlocks": ["autonomous_zone_events"]}'::jsonb, '[{"branch_id": "maintain_independence", "description": "Сохранить независимость", "consequences": {"skills": {"leadership": 50}, "reputation": {"Underground_Scene": 40, "Authorities": -30}, "unlocks": ["independent_zone_management"]}}, {"branch_id": "compromise_with_city", "description": "Пойти на компромисс с городом", "consequences": {"skills": {"social": 40}, "reputation": {"Authorities": 20, "Underground_Scene": -10}, "unlocks": ["city_integration_quests"]}}]'::jsonb, '{"metadata": {"id": "quest-berlin-2040-2060-techno-utopia", "title": "Берлин 2040-2060 — Техно-утопия", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "autonomy"], "topics": ["cultural_revolution", "communal_governance"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "github-issue-730", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/730", "relation": "migrated_to", "migrated_at": "2025-11-23T00:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2040-2060/quest-022-techno-utopia.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Высокой ветке Берлина нужен финальный сценарий о создании автономной техно-коммуны.", "goal": "Организовать район, управляемый музыкой и коллективными решениями, и удержать его от вмешательства властей.", "essence": "Игрок захватывает заброшенный квартал, настраивает звуковые системы, вводит демократию танца и защищает утопию.", "key_points": ["Техно используется как язык управления и голосование.", "Коллективные кухни и ресурсы требуют менеджмента.", "Давление властей и корпораций создаёт многовариантный финал."]}, "quest_definition": {"quest_type": "side", "level_min": 12, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "takeover_district", "text": "Захватить заброшенный квартал с союзниками и установить периметр", "type": "interact", "target": "district_takeover", "count": 1, "optional": false}, {"id": "negotiate_neutrality", "text": "Договориться с соседними сообществами о нейтралитете", "type": "interact", "target": "neighborhood_negotiations", "count": 3, "optional": false}, {"id": "setup_sound_network", "text": "Настроить звуковую сеть района: колонки, генераторы и энергобаланс", "type": "interact", "target": "sound_network_setup", "count": 1, "optional": false}, {"id": "implement_dance_governance", "text": "Запустить систему голосования танцем на рейвах", "type": "interact", "target": "dance_governance_system", "count": 1, "optional": false}, {"id": "defend_autonomy", "text": "Защитить автономию от давления властей: информационная кампания, переговоры или баррикады", "type": "interact", "target": "autonomy_defense", "count": 1, "optional": false}, {"id": "choose_utopia_outcome", "text": "Принять финальное решение: сохранить независимость, пойти на компромисс или потерять район", "type": "interact", "target": "utopia_final_choice", "count": 1, "optional": false}], "rewards": {"experience": 10000, "money": 0, "items": [{"item_id": "title_techno_utopia_founder", "quantity": 1}], "reputation": {}, "unlocks": ["autonomous_zone_events"]}, "branches": [{"branch_id": "maintain_independence", "description": "Сохранить независимость", "consequences": {"skills": {"leadership": 50}, "reputation": {"Underground_Scene": 40, "Authorities": -30}, "unlocks": ["independent_zone_management"]}}, {"branch_id": "compromise_with_city", "description": "Пойти на компромисс с городом", "consequences": {"skills": {"social": 40}, "reputation": {"Authorities": 20, "Underground_Scene": -10}, "unlocks": ["city_integration_quests"]}}]}, "content": {"sections": [{"id": "takeover", "title": "Захват района", "body": "Игрок с союзниками занимает заброшенные здания, устанавливает периметр и договаривается с соседними сообществами о нейтралитете.\\n", "mechanics_links": [], "assets": []}, {"id": "sound_network", "title": "Звуковая сеть", "body": "Район насыщается колонками и генераторами. Игрок балансирует энергопотребление и качество звука, открывая тематические сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "dance_governance", "title": "Голосование танцем", "body": "Решения принимаются на рейвах. Игрок запускает мини-игру «ритмического голосования», влияющую на экономику и безопасность.\\n", "mechanics_links": [], "assets": []}, {"id": "defense", "title": "Защита автономии", "body": "Власти предъявляют ультиматум. Игрок выбирает стратегию — информационная кампания, переговоры или баррикады.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Исход утопии", "body": "Финальный выбор: сохранить независимость, пойти на компромисс с городом или потерять район под давлением силовиков.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 10 000 XP, открытие автономной зоны для liveops-событий.\\n- Титул «Основатель техно-утопии» и доступ к уникальным сетам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 730, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики управления автономной зоной и защиты от властей."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Техно-утопия» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

