INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vienna-coffee-culture', 'Вена — Культура кофеен', 'Игрок проводит день в Café Central, заказывает венский Melange и взаимодействует с завсегдатаями для повышения интеллекта.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vienna-coffee-culture", "title": "Вена — Культура кофеен", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T20:32:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T20:32:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["vienna", "coffee", "lifestyle"], "topics": ["social_hubs", "intangible_heritage", "slow_life"], "related_systems": ["gathering-system", "mood-system", "reputation-system"], "related_documents": [{"id": "canon-quest-vienna-sachertorte", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/vienna/2020-2029/quest-004-coffee-house-culture.md", "visibility": "internal", "audience": ["narrative", "systems", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о венских кофейнях не отражал нематериальное наследие UNESCO и социальные механики.", "goal": "Сформировать опыт «кафе как гостиная города» с бонусами общения, знаний и влияния.", "essence": "Игрок проводит день в Café Central, заказывает венский Melange и взаимодействует с завсегдатаями для повышения интеллекта.", "key_points": ["Расписаны привычки посетителей, газеты на держателях и шахматные партии.", "Квест выдаёт культурные баффы и знакомит с историческими персонажами.", "Посещение кофеен связано с сетевой системой общения и миссиями slow life."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 2–4 часа.\\nНаграды: 1 500 XP, -15 едди, +20 культуры, бафф «Интеллект +30% на 8 часов».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить Café Central и ознакомиться с правилами Kaffeehauskultur.\\n2. Заказать классический Melange и десерт, читая газеты на деревянных держателях.\\n3. Присоединиться к партии в шахматы или дискуссии, повышая социальные связи.\\n4. Собирать истории завсегдатаев, включая знаменитых исторических клиентов.\\n5. Оставить запись в журнале кофейни и получить приглашение в другие кафе.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "gathering-system выдаёт коллекционные истории и рецепты напитков.\\nmood-system применяет эффект «Кофейная инспирация», усиливая интеллектуальные действия.\\nreputation-system повышает доверие культурных кругов и открывает доступ в закрытые клубы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Описаны традиции долгих бесед, роль кофеен в общественной жизни и вклад в нематериальное наследие UNESCO.\\nУпомянуты посетители, включая Фрейда и Троцкого, и атмосфера исторических интерьеров.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает ачивку «Завсегдатай кофеен» и скидки на напитки в сети городских кафе.\\nСобранные истории открывают дополнительные сюжетные крючки и знакомства.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": 