INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-bastille-revolution', 'Париж 2020-2029 — Революция Бастилии', 'Игроки собирают толпу на площади Бастилии, ведут марш по Парижу, штурмуют корпорационную цитадель и решают судьбу новой республики через три политических сценария.', 'main', 25, 50, '{"completed_quests": [], "flags": []}'::jsonb, '[{"id": "organize_rally", "type": "interaction", "description": "Организовать митинг на Place de la Bastille", "required": true}, {"id": "march_paris", "type": "location", "description": "Провести шествие по Парижу, расширяя поддержку", "required": true}, {"id": "storm_corporation", "type": "interaction", "description": "Штурмовать ключевое здание корпорации", "required": true}, {"id": "proclaim_republic", "type": "interaction", "description": "Провозгласить новую республику", "required": true}, {"id": "choose_political_course", "type": "choice", "description": "Выбрать дальнейший политический курс", "required": true}]'::jsonb, '{"experience": 10000, "money": {"type": "eddies", "value": 0}, "reputation": {}, "unlocks": {"achievements": [{"id": "paris_revolutionary", "name": "Революционер Парижа"}], "flags": []}}'::jsonb, '[{"id": "terror", "condition": "choose_political_course == ''terror''", "description": "Террор: радикальная чистка элиты"}, {"id": "moderation", "condition": "choose_political_course == ''moderation''", "description": "Умеренность: реформы без кровопролития"}, {"id": "compromise", "condition": "choose_political_course == ''compromise''", "description": "Компромисс: конституционная монархия"}]'::jsonb, '{"metadata": {"id": "canon-quest-paris-bastille-revolution", "title": "Париж 2020-2029 — Революция Бастилии", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T12:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["paris", "revolution", "politics"], "topics": ["world_events", "social_change"], "related_systems": ["narrative-service", "world-state-service"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "canon-quest-paris-notre-dame-restoration", "relation": "parallels"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/paris/2020-2029/quest-010-bastille-revolution.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Революционный сюжет находился в Markdown и не синхронизировался с пайплайнами знаний и очередями.", "goal": "Формализовать квест о штурме Бастилии в YAML, зафиксировав этапы восстания, выборы игроков и последствия.", "essence": "Игроки собирают толпу на площади Бастилии, ведут марш по Парижу, штурмуют корпорационную цитадель и решают судьбу новой республики через три политических сценария.", "key_points": [{"Структурированы ключевые этапы": "мобилизация, марш, штурм, провозглашение республики и выбор политического курса."}, {"Прописаны исторические отсылки": "лозунг «Liberté, Égalité, Fraternité», триколор, фригийский колпак и Марсельеза."}, "Описаны награды, политические последствия и включены глобальные механики восстания."]}, "quest_definition": {"quest_type": "main", "level_min": 25, "level_max": 50, "requirements": {"completed_quests": [], "flags": []}, "objectives": [{"id": "organize_rally", "type": "interaction", "description": "Организовать митинг на Place de la Bastille", "required": true}, {"id": "march_paris", "type": "location", "description": "Провести шествие по Парижу, расширяя поддержку", "required": true}, {"id": "storm_corporation", "type": "interaction", "description": "Штурмовать ключевое здание корпорации", "required": true}, {"id": "proclaim_republic", "type": "interaction", "description": "Провозгласить новую республику", "required": true}, {"id": "choose_political_course", "type": "choice", "description": "Выбрать дальнейший политический курс", "required": true}], "rewards": {"experience": 10000, "money": {"type": "eddies", "value": 0}, "reputation": {}, "unlocks": {"achievements": [{"id": "paris_revolutionary", "name": "Революционер Парижа"}], "flags": []}}, "branches": [{"id": "terror", "condition": "choose_political_course == ''terror''", "description": "Террор: радикальная чистка элиты"}, {"id": "moderation", "condition": "choose_political_course == ''moderation''", "description": "Умеренность: реформы без кровопролития"}, {"id": "compromise", "condition": "choose_political_course == ''compromise''", "description": "Компромисс: конституционная монархия"}]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест переносит игроков в атмосферу новой французской революции, соединяя исторические символы 1789 года с\\nкорпо-киберпанковым Парижем 2029 года.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы восстания", "body": "1. Организовать митинг на Place de la Bastille и подготовить символику восстания.\\n2. Провести шествие по Парижу, расширяя поддержку и собирая баррикады.\\n3. Штурмовать ключевое здание корпорации, используя массовые и боевые механики.\\n4. Провозгласить новую республику вместе с союзниками.\\n5. Выбрать дальнейший политический курс: Террор, Умеренность или Компромисс.\\n", "mechanics_links": [], "assets": []}, {"id": "choices_and_consequences", "title": "Выборы и последствия", "body": "- Террор: радикальная чистка элиты, усиление контроля и международные санкции.\\n- Умеренность: реформы без кровопролития, сохранение стабильности и рост поддержки граждан.\\n- Компромисс: конституционная монархия, баланс между элитами и революционерами.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Отсылки включают триколор, фригийские колпаки, лозунг «Aux armes, citoyens!» и баррикады на улицах. Присутствие\\n«Марсельезы» подчёркивает связь с оригинальным восстанием.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "Квест выдаёт 10 000 XP и титул «Революционер Парижа». Последствия меняют политический баланс региона и активируют\\nглобальные события в Париже.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 620, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-paris-bastille-revolution"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Революция Бастилии» в структуру знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches (включая политические ветки). Обновлен github_issue на 620."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

