INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vienna-2029-006-spanish-riding-school', 'Вена: Испанская школа верховой езды', 'Игрок попадает на балет лошадей, знакомится с мастерами верховой езды и историей Lipizzaner.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vienna-2029-006-spanish-riding-school", "title": "Вена: Испанская школа верховой езды", "document_type": "canon", "category": "city-quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T06:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vienna", "culture", "equestrian"], "topics": ["city-lore"], "related_systems": ["social-mechanics"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/vienna/2020-2029/quest-006-spanish-riding-school.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не структурирован культурный квест о показе Lipizzaner в Испанской школе верховой езды.", "goal": "Описать опыт посещения Hofburg, представление и культурную ценность традиции на 450 лет.", "essence": "Игрок попадает на балет лошадей, знакомится с мастерами верховой езды и историей Lipizzaner.", "key_points": ["Доступ к легендарной школе 1572 года в Hofburg.", "Наблюдение за элементами классической выездки и историческими костюмами.", "Рост культурного рейтинга и усиление связи с венской линией квестов."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `VIENNA-2029-006`\\n- Локация: Hofburg, Spanish Riding School.\\n- Формат: культурное событие, продолжительность 2–4 часа.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "stages", "title": "Этапы визита", "body": "1. Войти в дворец Hofburg и занять место в манеже.\\n2. Наблюдать выступление Lipizzaner: академическая выездка, балет трюков, исторические костюмы.\\n3. Посетить музей школы и изучить историю династии лошадей.\\n4. Завершить встречей с тренером и получить памятный жетон.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "cultural_notes", "title": "Культурные детали", "body": "- Школа основана в 1572 году и является символом императорской традиции.\\n- Lipizzaner считаются национальным достоянием Австрии.\\n- Представление сочетает музыку, балет и военное наследие кавалерии.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 2000 XP, расходы 50 евро на билет.\\n- Культурный показатель +25.\\n- Ачивка «Наездник Липицианов».\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста про Испанскую школу верховой езды в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

