INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-champs-elysees', 'Париж 2020-2029 — Елисейские Поля', 'Игрок проходит главную авеню Парижа от Place de la Concorde до Arc de Triomphe, взаимодействуя с кафе и люксовыми бутиками.', 'exploration', 3, 50, '{"completed_quests": [], "flags": []}'::jsonb, '[{"id": "start_concorde", "type": "location", "description": "Стартовать у Place de la Concorde", "required": true}, {"id": "walk_avenue", "type": "location", "description": "Пройти по авеню, посещая кафе и luxury-бутики", "required": true}, {"id": "shop_interaction", "type": "interaction", "description": "Провести интерактивный шопинг или window-shopping", "required": false}, {"id": "climb_arc", "type": "location", "description": "Подняться на Arc de Triomphe", "required": true}]'::jsonb, '{"experience": 800, "money": {"type": "eddies", "value": -10000}, "reputation": {"style": 15}, "unlocks": {"achievements": [{"id": "champs_elysees_shopping", "name": "Шопинг на Елисейских"}], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-champs-elysees", "title": "Париж 2020-2029 — Елисейские Поля", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["paris", "luxury", "shopping"], "topics": ["lifestyle", "tourism"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "content-world-quests-western-europe", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/paris/2020-2029/quest-004-champs-elysees.md", "visibility": "internal", "audience": ["narrative", "content", "vision"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Прогулка по Елисейским полям была в Markdown и не интегрировалась с пайплайном нарративных квестов.", "goal": "Структурировать lifestyle-квест о Champs-Élysées в YAML с этапами, экономическими последствиями и культурными деталями.", "essence": "Игрок проходит главную авеню Парижа от Place de la Concorde до Arc de Triomphe, взаимодействуя с кафе и люксовыми бутиками.", "key_points": ["Этапы включают прогулку, посещение магазинов и подъём на Триумфальную арку.", "Фиксированы элементы парада 14 июля, витрины брендов и парижский шик.", "Награды дают стиль, а затраты отражают люксовый шопинг."]}, "quest_definition": {"quest_type": "exploration", "level_min": 3, "level_max": 50, "requirements": {"completed_quests": [], "flags": []}, "objectives": [{"id": "start_concorde", "type": "location", "description": "Стартовать у Place de la Concorde", "required": true}, {"id": "walk_avenue", "type": "location", "description": "Пройти по авеню, посещая кафе и luxury-бутики", "required": true}, {"id": "shop_interaction", "type": "interaction", "description": "Провести интерактивный шопинг или window-shopping", "required": false}, {"id": "climb_arc", "type": "location", "description": "Подняться на Arc de Triomphe", "required": true}], "rewards": {"experience": 800, "money": {"type": "eddies", "value": -10000}, "reputation": {"style": 15}, "unlocks": {"achievements": [{"id": "champs_elysees_shopping", "name": "Шопинг на Елисейских"}], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест демонстрирует Champs-Élysées как «самую красивую авеню мира», объединяя туристический маршрут с элементами\\nluxury-шопинга и исторических символов Парижа.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы прогулки", "body": "1. Стартовать у Place de la Concorde и оценить архитектуру площади.\\n2. Пройти по авеню, посещая кафе и luxury-бутики (Louis Vuitton, Cartier и др.).\\n3. Провести интерактивный шопинг или window-shopping.\\n4. Подняться на Arc de Triomphe и наблюдать радиальную перспективу 12 авеню.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Подсвечены аллеи деревьев, террасы кафе, витрины модных домов и символика парада на День Бастилии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "Игрок получает 800 XP, может потратить до 10 000 едди на люксовые товары, увеличивает параметр «Стиль» на 15 и получает\\nдостижение «Шопинг на Елисейских». Механики включают туристическую прогулку, торговые взаимодействия и атмосферу парижского шика.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 614, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-paris-champs-elysees"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Елисейские Поля» в формат знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 614."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURREN