-- Quest: canon-quest-detroit-revival-hope
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-detroit-revival-hope', 'Детройт 2020-2029 — Надежда на возрождение', 'Игрок исследует downtown-реновации, контраст с окраинами и принимает позицию по поводу будущего города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-detroit-revival-hope", "title": "Детройт 2020-2029 — Надежда на возрождение", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-05T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["detroit", "economy", "urban-renewal"], "topics": ["regional-history", "socio-economics"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "canon-quest-detroit-motor-city", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-341", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/341", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/detroit/2020-2029/quest-010-revival-hope.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий «Надежда на возрождение» не был оформлен в структурированном виде.", "goal": "Формализовать квест о восстановлении Детройта для привязки к экономическим и социальным аркам.", "essence": "Игрок исследует downtown-реновации, контраст с окраинами и принимает позицию по поводу будущего города.", "key_points": ["Показаны инвестиции Dan Gilbert, рост стартапов и джентрификация.", "Этапы раскрывают контраст центра и окраин, влияние артистов и дешёвой недвижимости.", "Награды повышают «Надежду» и выдают достижение «Свидетель Возрождения»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест исследует socio-economic возрождение Детройта в конце 2020-х, демонстрируя новые бизнесы, арт-сообщества и влияние\\nинвесторов. Игрок оценивает перспективы города через выбор оптимизма или скептицизма.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Осмотр обновлённого Downtown с кафе, офисами и арт-пространствами.\\n2. Сравнение центра и окраин, где продолжается упадок.\\n3. Рассказ об артистах, покупающих дома за $1000, и росте стартап-сцены.\\n4. Финальный выбор: поддержать оптимистов или подчеркнуть скепсис.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Подчёркиваются инвестиции Dan Gilbert, дешёвая недвижимость и кампания «Detroit Hustles Harder». Квест фиксирует медленный\\nхарактер восстановления и его социальные последствия.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "Игрок получает 3000 XP, параметр «Надежда» увеличивается на 20, выдаётся достижение «Свидетель Возрождения». Механики —\\nсоциально-экономический анализ, исследование города и моральный выбор.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 341, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-detroit-revival-hope"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Надежда на возрождение» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

