INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2061-2077-hacker-collective', 'Берлин 2061-2077 — Хакерский коллектив CCC', 'Игрок проходит испытания, участвует в хакатоне и публично раскрывает уязвимость, получая доступ к ресурсам клуба.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2061-2077-hacker-collective", "title": "Берлин 2061-2077 — Хакерский коллектив CCC", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:12:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "chaos-computer-club", "hackers"], "topics": ["faction_progression", "hacker_ethics"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "hacking-service", "reputation-service"], "related_documents": [{"id": "github-issue-740", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/740", "relation": "migrated_to", "migrated_at": "2025-11-23T00:12:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2061-2077/quest-032-hacker-collective.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Хакерской ветке Берлина нужен квест о вступлении в легендарный Chaos Computer Club.", "goal": "Проверить навыки, пройти конгресс и доказать соответствие хакерской этике CCC.", "essence": "Игрок проходит испытания, участвует в хакатоне и публично раскрывает уязвимость, получая доступ к ресурсам клуба.", "key_points": ["Серия испытаний демонстрирует культурные ритуалы CCC.", "Хакатон и публичная презентация поднимают репутацию игрока.", "Вступление открывает фракционные миссии и ресурсы."]}, "content": {"sections": [{"id": "aptitude_test", "title": "Тест навыков", "body": "Игрок получает приглашение на закрытый тест: нужно взломать виртуальную песочницу, не нарушив хакерскую этику. Результат определяет\\nотношение членов клуба.\\n", "mechanics_links": ["mechanics/hacking/aptitude-challenge.yaml"], "assets": []}, {"id": "congress", "title": "Chaos Communication Congress", "body": "Посещение легендарного конгресса CCC. Игрок слушает доклады, участвует в Blinkenlights-инсталляциях и знакомится с ветеранами сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "hackathon", "title": "Ночная сессия", "body": "24-часовой хакатон по обнаружению уязвимости в корпоративной системе. Успех приносит очки репутации и данные для дальнейшего квеста.\\n", "mechanics_links": ["mechanics/hacking/team-hackathon.yaml"], "assets": []}, {"id": "disclosure", "title": "Публичное раскрытие", "body": "Игрок решает, как презентовать найденную дыру: официально, через медиапартнёров или анонимно. Выбор влияет на отношения с корпорациями.\\n", "mechanics_links": ["mechanics/narrative/public-disclosure.yaml"], "assets": []}, {"id": "initiation", "title": "Вступительный ритуал", "body": "Финальная сцена — вручение бейджа CCC и подписание хакерского кодекса. Игрок получает доступ к офлайн- и онлайн-ресурсам клуба.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 8 000 XP, доступ к лабораториям CCC и их контрактам.\\n- Титул «Chaos Hacker» и ветка фракционных миссий.\\n", "mechanics_links": ["mechanics/progression/faction-unlock.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 740, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Хакерский коллектив CCC» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

