INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-rome-2029-004-pasta-master', 'Рим: мастер-паста от nonna', 'Игрок изучает четыре классические пасты, управляет ресурсами кухни и выбирает, делиться ли секретами семьи.', 'side', 5, 20, '[{"type": "location", "value": "rome"}]'::jsonb, '[{"id": "prepare_ingredients", "type": "collect", "description": "Подготовить ингредиенты с учетом ограниченных поставок", "required": true}, {"id": "make_pasta", "type": "complete", "description": "Приготовить четыре классические пасты (Carbonara, Amatriciana, Cacio e Pepe, Gricia)", "required": true}, {"id": "choose_strategy", "type": "choose", "description": "Выбрать стратегию для рецептов", "required": true}]'::jsonb, '{"experience": 2500, "money": {"type": "eddies", "value": -10}, "reputation": {"culinary": 15}, "unlocks": {"achievements": [{"id": "pasta_maestro", "name": "Pasta Maestro"}], "flags": ["roman_culinary_heritage_unlocked", "gourmet_faction_recipes_unlocked"]}}'::jsonb, '[{"id": "keep_tradition", "name": "Сохранить традицию", "description": "Оставить рецепты в семье"}, {"id": "sell_masterclass", "name": "Продать мастер-класс", "description": "Продать эксклюзивный мастер-класс туристическому агентству"}, {"id": "create_video", "name": "Записать видеокурс", "description": "Записать видеокурс и запускать сезонные ивенты"}]'::jsonb, '{"metadata": {"id": "quest-rome-2029-004-pasta-master", "title": "Рим: мастер-паста от nonna", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["rome", "cuisine", "tradition"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "economy-service", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/rome/2020-2029/quest-004-pasta-master.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик обучающего квеста не включал механики готовки и экономический эффект от семейных рецептов.", "goal": "Структурировать мастер-класс у римской nonna с пошаговыми проверками и возможностью монетизации рецептов.", "essence": "Игрок изучает четыре классические пасты, управляет ресурсами кухни и выбирает, делиться ли секретами семьи.", "key_points": ["Пошаговые мини-игры по замесу теста и приготовлению соусов.", {"Выбор стратегии": "сохранить традицию или вывести рецепт на глобальный рынок."}, "Квест усиливает гастрономическую ветку Рима и даёт долговременный бафф."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": 20, "requirements": [{"type": "location", "value": "rome"}], "objectives": [{"id": "prepare_ingredients", "type": "collect", "description": "Подготовить ингредиенты с учетом ограниченных поставок", "required": true}, {"id": "make_pasta", "type": "complete", "description": "Приготовить четыре классические пасты (Carbonara, Amatriciana, Cacio e Pepe, Gricia)", "required": true}, {"id": "choose_strategy", "type": "choose", "description": "Выбрать стратегию для рецептов", "required": true}], "rewards": {"experience": 2500, "money": {"type": "eddies", "value": -10}, "reputation": {"culinary": 15}, "unlocks": {"achievements": [{"id": "pasta_maestro", "name": "Pasta Maestro"}], "flags": ["roman_culinary_heritage_unlocked", "gourmet_faction_recipes_unlocked"]}}, "branches": [{"id": "keep_tradition", "name": "Сохранить традицию", "description": "Оставить рецепты в семье"}, {"id": "sell_masterclass", "name": "Продать мастер-класс", "description": "Продать эксклюзивный мастер-класс туристическому агентству"}, {"id": "create_video", "name": "Записать видеокурс", "description": "Записать видеокурс и запускать сезонные ивенты"}]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ROME-2029-004`\\n- Формат: гастрономический мастер-класс, сложность средняя, solo, длительность 2–4 часа.\\n- Локация: домашняя траттория в районе Трастевере.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "workshop", "title": "Мастерская nonna", "body": "1. Подготовить ингредиенты с учётом ограниченных поставок.\\n2. Замешивать тесто вручную, проходя серию проверок на тайминг.\\n3. Приготовить Carbonara, Amatriciana, Cacio e Pepe и Gricia под контролем наставницы.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "choices", "title": "Решения игрока", "body": "- Оставить рецепты в семье и получить доверие локальной общины.\\n- Продать эксклюзивный мастер-класс туристическому агентству (рост economy-service).\\n- Записать видеокурс и запускать сезонные ивенты через progression-service.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 2500 XP, расходы 10 кредитов на ингредиенты.\\n- Бафф «Pasta Maestro» (эффективность кулинарных баффов +20% на 48 часов).\\n- Доступ к линии «Roman Culinary Heritage» и рецептам для фракции гурманов.\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 606, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Оформлен мастер-класс по римской пасте в формат знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 606."}], "validati