INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2078-2093-personality-conflict', 'Berlin 2078-2093 — Конфликт личностей', 'Квест на управление конфликтами между энграммами. Игрок должен принять решение о том,
